"use client"

import Link from "next/link"
import { usePathname } from "next/navigation"
import { cn } from "@/lib/utils"
import {
    LayoutDashboard,
    Users,
    BookOpen,
    FileText,
    Video,
    Award,
    CreditCard,
    Settings,
    MessageSquare,
    Calendar,
    Menu,
    X,
    LogOut,
    BrainCircuit,
    Sparkles,
    VideoIcon,
    ClipboardCheck,
    ChevronDown,
    ChevronRight,
    DollarSign,
    BarChart3,
    LineChart,
    Magnet,
    GraduationCap,
    Bot,
    Briefcase
} from "lucide-react"
import { Button } from "@/components/ui/button"
import { ScrollArea } from "@/components/ui/scroll-area"
import { useAuth } from "@/lib/auth-context"
import { useState, useEffect } from "react"

interface SidebarProps extends React.HTMLAttributes<HTMLDivElement> {
    isCollapsed?: boolean // Added member to avoid empty interface warning
}

export function AdminSidebar({ className }: SidebarProps) {
    const pathname = usePathname()
    const { logout, hasPermission, user } = useAuth()
    const [isMobileOpen, setIsMobileOpen] = useState(false)
    const [expandedMenus, setExpandedMenus] = useState<string[]>([])

    // Close mobile menu on route change
    useEffect(() => {
        if (isMobileOpen) {
            // Defer to avoid cascading render lint
            setTimeout(() => setIsMobileOpen(false), 0)
        }
    }, [pathname, isMobileOpen])

    // Auto-expand active parent menu
    useEffect(() => {
        function getRoutesInternal() {
            const routesList = [
                {
                    label: 'Dashboard',
                    href: '/admin',
                    icon: LayoutDashboard
                },
                {
                    label: 'User Management',
                    icon: Users,
                    subItems: [
                        { label: 'All Users', href: '/admin/users' },
                        { label: 'Courses', href: '/admin/courses' },
                        { label: 'Certificates', href: '/admin/certificates' },
                    ]
                },
                {
                    label: 'Leads & CRM',
                    icon: Magnet,
                    subItems: [
                        { label: 'All Leads', href: '/admin/leads' },
                        { label: 'Meetings', href: '/admin/meetings' },
                        { label: 'Tasks', href: '/admin/tasks' },
                    ]
                },
                {
                    label: 'Learning CMS',
                    icon: GraduationCap,
                    subItems: [
                        { label: 'Blogs', href: '/admin/blogs' },
                        { label: 'Reviews', href: '/admin/reviews' },
                    ]
                },
                {
                    label: 'AI Agents',
                    icon: Bot,
                    subItems: [
                        { label: 'Configurations', href: '/admin/ai' },
                        { label: 'Training Data', href: '/admin/ai/training' },
                    ]
                },
                {
                    label: 'Finance',
                    icon: CreditCard,
                    subItems: [
                        { label: 'Payments', href: '/admin/payments' },
                    ]
                },
                {
                    label: 'Operations',
                    icon: Briefcase,
                    subItems: [
                        { label: 'Job Board', href: '/admin/jobs' },
                    ]
                },
                {
                    label: 'Technical',
                    icon: Settings,
                    subItems: [
                        { label: 'Video Settings', href: '/admin/video-settings' },
                        { label: 'System Logs', href: '/admin/logs' },
                    ]
                }
            ]
            return routesList
        }

        const routes = getRoutesInternal()
        routes.forEach(route => {
            if (route.subItems && route.subItems.some(sub => pathname.startsWith(sub.href))) {
                if (!expandedMenus.includes(route.label)) {
                    setExpandedMenus(prev => [...prev, route.label])
                }
            }
        })
    }, [pathname])

    const toggleMenu = (label: string) => {
        setExpandedMenus(prev =>
            prev.includes(label)
                ? prev.filter(l => l !== label)
                : [...prev, label]
        )
    }

    const getRoutes = () => {
        const routes = [
            {
                label: "Dashboard",
                icon: LayoutDashboard,
                href: "/admin",
                active: pathname === "/admin",
                // Dashboard is always visible to staff, but maybe limited widgets
            },
            {
                label: "Leads & CRM",
                icon: MessageSquare,
                href: "/admin/leads",
                active: pathname.startsWith("/admin/leads"),
                permission: 'VIEW_LEADS'
            },
            {
                label: "Tasks",
                icon: Calendar,
                href: "/admin/tasks",
                active: pathname.startsWith("/admin/tasks"),
                permission: 'VIEW_TASKS'
            },
            {
                label: "Courses",
                icon: BookOpen,
                href: "/admin/courses",
                active: pathname.startsWith("/admin/courses"),
                permission: 'MANAGE_COURSES',
                subItems: [
                    { label: "All Courses", href: "/admin/courses" },
                    { label: "Create New", href: "/admin/courses/new" },
                    { label: "Review Queue", href: "/admin/courses/review" },
                    { label: "Live Classes", href: "/admin/courses/live" },
                ]
            },
            {
                label: "Blog Manager",
                icon: FileText,
                href: "/admin/blogs",
                active: pathname.startsWith("/admin/blogs"),
                permission: 'MANAGE_CONTENT'
            },
            {
                label: "AI Settings",
                icon: Sparkles,
                href: "/admin/ai",
                active: pathname.startsWith("/admin/ai"),
                permission: 'MANAGE_SETTINGS',
                subItems: [
                    { label: "API Configuration", href: "/admin/ai" },
                    { label: "Knowledge Base", href: "/admin/ai/knowledge-base" },
                    { label: "Q&A Training", href: "/admin/ai/training" },
                ]
            },
            {
                label: "AI Interviews",
                icon: BrainCircuit,
                href: "/admin/ai-interviews",
                active: pathname.startsWith("/admin/ai-interviews"),
                permission: 'MANAGE_SETTINGS'
            },
            {
                label: "Video Integration",
                icon: VideoIcon,
                href: "/admin/video-settings",
                active: pathname.startsWith("/admin/video-settings"),
                permission: 'MANAGE_SETTINGS'
            },
            {
                label: "Interviews",
                icon: Video,
                href: "/admin/interviews",
                active: pathname.startsWith("/admin/interviews"),
                permission: 'VIEW_INTERVIEWS'
            },
            {
                label: "Users & Roles",
                icon: Users,
                href: "/admin/users",
                active: pathname.startsWith("/admin/users"),
                permission: 'MANAGE_USERS'
            },
            {
                label: "Certificates",
                icon: Award,
                href: "/admin/certificates",
                active: pathname.startsWith("/admin/certificates"),
                permission: 'MANAGE_CERTIFICATES'
            },
            {
                label: "Pricing & Plans",
                icon: CreditCard,
                href: "/admin/pricing",
                active: pathname.startsWith("/admin/pricing"),
                permission: 'VIEW_FINANCE'
            },
            {
                label: "System Settings",
                icon: Settings,
                href: "/admin/settings",
                active: pathname.startsWith("/admin/settings"),
                permission: 'MANAGE_SETTINGS',
                subItems: [
                    { label: "General Settings", href: "/admin/settings" },
                    { label: "Email Services", href: "/admin/email-settings" },
                    { label: "Video Integration", href: "/admin/video-settings" },
                    { label: "Lead Integrations", href: "/admin/leads/integrations" },
                ]
            },
            {
                label: "Support Tickets",
                icon: MessageSquare,
                href: "/admin/support",
                active: pathname.startsWith("/admin/support"),
                permission: 'VIEW_SUPPORT'
            },
            {
                label: "Employer Requests",
                icon: Users,
                href: "/admin/employers",
                active: pathname.startsWith("/admin/employers"),
                permission: 'MANAGE_USERS'
            },
        ]

        // Filter routes based on permissions
        return routes.filter(route => {
            if (!route.permission) return true
            return hasPermission(route.permission)
        })
    }

    const routes = getRoutes()

    return (
        <>
            {/* Mobile Trigger */}
            <div className="md:hidden fixed top-4 right-4 z-[9999]">
                <Button
                    variant="outline"
                    size="icon"
                    onClick={() => setIsMobileOpen(!isMobileOpen)}
                    title={isMobileOpen ? "Close menu" : "Open menu"}
                >
                    {isMobileOpen ? <X className="h-4 w-4" /> : <Menu className="h-4 w-4" />}
                </Button>
            </div>

            <div className={cn(
                "min-h-screen border-r bg-background fixed left-0 top-0 bottom-0 z-40 w-64 transition-transform duration-300 md:translate-x-0 flex flex-col",
                isMobileOpen ? "translate-x-0" : "-translate-x-full",
                className
            )}>
                {/* Header - Fixed */}
                <div className="px-6 py-4 border-b flex-shrink-0">
                    <h2 className="text-2xl font-bold tracking-tight text-primary">Admin Panel</h2>
                    <p className="text-xs text-muted-foreground mt-1">Super Admin Console</p>
                </div>

                {/* Scrollable Menu Area */}
                <ScrollArea className="flex-1 py-4 px-3">
                    <nav className="space-y-1">
                        {routes.map((route) => (
                            <div key={route.href}>
                                {route.subItems ? (
                                    <>
                                        <button
                                            onClick={() => toggleMenu(route.label)}
                                            className={cn(
                                                "text-sm group flex p-3 w-full justify-between items-center font-medium cursor-pointer hover:text-primary hover:bg-primary/10 rounded-lg transition",
                                                route.active ? "text-primary bg-primary/10" : "text-muted-foreground"
                                            )}
                                        >
                                            <div className="flex items-center">
                                                <route.icon className={cn("h-5 w-5 mr-3", route.active ? "text-primary" : "text-muted-foreground")} />
                                                {route.label}
                                            </div>
                                            {expandedMenus.includes(route.label) ? (
                                                <ChevronDown className="h-4 w-4" />
                                            ) : (
                                                <ChevronRight className="h-4 w-4" />
                                            )}
                                        </button>
                                        {expandedMenus.includes(route.label) && (
                                            <div className="ml-8 mt-1 space-y-1">
                                                {route.subItems.map((sub) => (
                                                    <Link
                                                        key={sub.href}
                                                        href={sub.href}
                                                        className={cn(
                                                            "text-sm flex p-2 rounded-md transition hover:bg-primary/5",
                                                            pathname === sub.href ? "text-primary font-medium" : "text-muted-foreground"
                                                        )}
                                                    >
                                                        {sub.label}
                                                    </Link>
                                                ))}
                                            </div>
                                        )}
                                    </>
                                ) : (
                                    <Link
                                        href={route.href}
                                        className={cn(
                                            "text-sm group flex p-3 w-full justify-start font-medium cursor-pointer hover:text-primary hover:bg-primary/10 rounded-lg transition",
                                            route.active ? "text-primary bg-primary/10" : "text-muted-foreground"
                                        )}
                                    >
                                        <div className="flex items-center flex-1">
                                            <route.icon className={cn("h-5 w-5 mr-3", route.active ? "text-primary" : "text-muted-foreground")} />
                                            {route.label}
                                        </div>
                                    </Link>
                                )}
                            </div>
                        ))}
                    </nav>
                </ScrollArea>

                {/* Bottom Actions - Fixed */}
                <div className="border-t p-3 flex-shrink-0">
                    <Button
                        variant="ghost"
                        className="w-full justify-start text-red-500 hover:text-red-600 hover:bg-red-50"
                        onClick={logout}
                    >
                        <LogOut className="h-5 w-5 mr-3" />
                        Sign Out
                    </Button>
                </div>
            </div>

            {/* Mobile Overlay */}
            {isMobileOpen && (
                <div
                    className="fixed inset-0 bg-black/50 z-30 md:hidden"
                    onClick={() => setIsMobileOpen(false)}
                />
            )}
        </>
    )
}

"use client"

import * as React from 'react'
import { useState, useRef, useCallback } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Progress } from '@/components/ui/progress'
import {
    Video,
    StopCircle,
    PlayCircle,
    Upload,
    ScreenShare,
    Camera,
    Mic,
    MicOff,
    Monitor,
    Trash2,
    Download,
    Loader2,
    AlertCircle
} from 'lucide-react'

interface VideoRecorderProps {
    onRecordingComplete?: (blob: Blob, thumbnail?: string) => void
    onUploadComplete?: (url: string, thumbnail?: string) => void
    maxDuration?: number // in seconds
    allowScreenShare?: boolean
}

type RecordingMode = 'camera' | 'screen' | 'screen_with_camera'

export function VideoRecorder({
    onRecordingComplete,
    onUploadComplete,
    maxDuration = 600, // 10 minutes default
    allowScreenShare = true
}: VideoRecorderProps) {
    const [isRecording, setIsRecording] = useState(false)
    const [isPreviewing, setIsPreviewing] = useState(false)
    const [recordedBlob, setRecordedBlob] = useState<Blob | null>(null)
    const [recordedUrl, setRecordedUrl] = useState<string | null>(null)
    const [duration, setDuration] = useState(0)
    const [mode, setMode] = useState<RecordingMode>('camera')
    const [isMuted, setIsMuted] = useState(false)
    const [isUploading, setIsUploading] = useState(false)
    const [error, setError] = useState<string | null>(null)

    const videoRef = useRef<HTMLVideoElement>(null)
    const previewRef = useRef<HTMLVideoElement>(null)
    const mediaRecorderRef = useRef<MediaRecorder | null>(null)
    const streamRef = useRef<MediaStream | null>(null)
    const chunksRef = useRef<Blob[]>([])
    const timerRef = useRef<NodeJS.Timeout | null>(null)

    const stopRecording = useCallback(() => {
        if (timerRef.current) {
            clearInterval(timerRef.current)
            timerRef.current = null
        }

        if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
            mediaRecorderRef.current.stop()
        }

        if (streamRef.current) {
            streamRef.current.getTracks().forEach(track => track.stop())
            streamRef.current = null
        }

        setIsRecording(false)
    }, [])

    const startRecording = useCallback(async () => {
        setError(null)
        chunksRef.current = []

        try {
            let stream: MediaStream

            if (mode === 'camera') {
                stream = await navigator.mediaDevices.getUserMedia({
                    video: { width: 1280, height: 720, facingMode: 'user' },
                    audio: true
                })
            } else if (mode === 'screen') {
                stream = await navigator.mediaDevices.getDisplayMedia({
                    video: { width: 1920, height: 1080 },
                    audio: true
                })
            } else {
                // Screen with camera overlay
                const screenStream = await navigator.mediaDevices.getDisplayMedia({
                    video: { width: 1920, height: 1080 },
                    audio: true
                })
                const cameraStream = await navigator.mediaDevices.getUserMedia({
                    video: { width: 320, height: 240 },
                    audio: true
                })

                // Combine streams
                const tracks = [
                    ...screenStream.getVideoTracks(),
                    ...cameraStream.getAudioTracks()
                ]
                stream = new MediaStream(tracks)
            }

            streamRef.current = stream

            if (videoRef.current) {
                videoRef.current.srcObject = stream
                videoRef.current.muted = true
                await videoRef.current.play()
            }

            const mediaRecorder = new MediaRecorder(stream, {
                mimeType: 'video/webm;codecs=vp9'
            })

            mediaRecorder.ondataavailable = (e) => {
                if (e.data.size > 0) {
                    chunksRef.current.push(e.data)
                }
            }

            mediaRecorder.onstop = () => {
                const blob = new Blob(chunksRef.current, { type: 'video/webm' })
                setRecordedBlob(blob)
                const url = URL.createObjectURL(blob)
                setRecordedUrl(url)
                setIsPreviewing(true)

                // Generate thumbnail
                if (videoRef.current) {
                    const canvas = document.createElement('canvas')
                    canvas.width = videoRef.current.videoWidth
                    canvas.height = videoRef.current.videoHeight
                    const ctx = canvas.getContext('2d')
                    if (ctx) {
                        ctx.drawImage(videoRef.current, 0, 0)
                        const thumbnail = canvas.toDataURL('image/jpeg', 0.7)
                        onRecordingComplete?.(blob, thumbnail)
                    }
                }
            }

            mediaRecorderRef.current = mediaRecorder
            mediaRecorder.start(1000) // Collect data every second
            setIsRecording(true)
            setDuration(0)

            // Start duration timer
            timerRef.current = setInterval(() => {
                setDuration(prev => {
                    if (prev >= maxDuration) {
                        stopRecording()
                        return prev
                    }
                    return prev + 1
                })
            }, 1000)

        } catch (err: unknown) {
            console.error('Failed to start recording:', err)
            const message = (err as { message?: string })?.message || 'Failed to access camera/microphone'
            setError(message)
        }
    }, [mode, maxDuration, onRecordingComplete, stopRecording])

    const discardRecording = useCallback(() => {
        if (recordedUrl) {
            URL.revokeObjectURL(recordedUrl)
        }
        setRecordedBlob(null)
        setRecordedUrl(null)
        setIsPreviewing(false)
        setDuration(0)
    }, [recordedUrl])

    const downloadRecording = useCallback(() => {
        if (!recordedBlob) return
        const url = URL.createObjectURL(recordedBlob)
        const a = document.createElement('a')
        a.href = url
        a.download = `recording_${Date.now()}.webm`
        a.click()
        URL.revokeObjectURL(url)
    }, [recordedBlob])

    const uploadRecording = useCallback(async () => {
        if (!recordedBlob) return
        setIsUploading(true)

        try {
            const formData = new FormData()
            formData.append('video', recordedBlob, 'recording.webm')

            // Mock upload - replace with actual API call
            await new Promise(resolve => setTimeout(resolve, 2000))
            const mockUrl = `https://storage.example.com/videos/${Date.now()}.webm`

            onUploadComplete?.(mockUrl)
            setIsUploading(false)
        } catch (err) {
            setError('Failed to upload recording')
            setIsUploading(false)
        }
    }, [recordedBlob, onUploadComplete])

    const toggleMute = useCallback(() => {
        if (streamRef.current) {
            streamRef.current.getAudioTracks().forEach(track => {
                track.enabled = isMuted
            })
        }
        setIsMuted(!isMuted)
    }, [isMuted])

    const formatTime = (seconds: number) => {
        const mins = Math.floor(seconds / 60)
        const secs = seconds % 60
        return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`
    }

    return (
        <Card>
            <CardHeader>
                <CardTitle className="flex items-center gap-2">
                    <Video className="h-5 w-5" />
                    Video Recorder
                </CardTitle>
                <CardDescription>
                    Record video from camera or capture your screen
                </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
                {error && (
                    <div className="p-3 bg-red-50 border border-red-200 rounded-lg flex items-center gap-2 text-red-700">
                        <AlertCircle className="h-4 w-4" />
                        <span className="text-sm">{error}</span>
                    </div>
                )}

                {/* Mode Selector */}
                {!isRecording && !isPreviewing && (
                    <div className="flex gap-2">
                        <Button
                            variant={mode === 'camera' ? 'default' : 'outline'}
                            size="sm"
                            onClick={() => setMode('camera')}
                        >
                            <Camera className="h-4 w-4 mr-2" />
                            Camera
                        </Button>
                        {allowScreenShare && (
                            <>
                                <Button
                                    variant={mode === 'screen' ? 'default' : 'outline'}
                                    size="sm"
                                    onClick={() => setMode('screen')}
                                >
                                    <Monitor className="h-4 w-4 mr-2" />
                                    Screen
                                </Button>
                                <Button
                                    variant={mode === 'screen_with_camera' ? 'default' : 'outline'}
                                    size="sm"
                                    onClick={() => setMode('screen_with_camera')}
                                >
                                    <ScreenShare className="h-4 w-4 mr-2" />
                                    Screen + Cam
                                </Button>
                            </>
                        )}
                    </div>
                )}

                {/* Video Preview */}
                <div className="relative aspect-video bg-black rounded-lg overflow-hidden">
                    {isPreviewing && recordedUrl ? (
                        <video
                            ref={previewRef}
                            src={recordedUrl}
                            controls
                            className="w-full h-full object-contain"
                        />
                    ) : (
                        <video
                            ref={videoRef}
                            className="w-full h-full object-contain"
                            autoPlay
                            muted
                            playsInline
                        />
                    )}

                    {/* Recording Indicator */}
                    {isRecording && (
                        <div className="absolute top-4 left-4 flex items-center gap-2 bg-red-600 text-white px-3 py-1 rounded-full">
                            <span className="w-2 h-2 bg-white rounded-full animate-pulse" />
                            <span className="text-sm font-medium">REC</span>
                            <span className="text-sm">{formatTime(duration)}</span>
                        </div>
                    )}

                    {/* Duration Progress */}
                    {isRecording && (
                        <div className="absolute bottom-0 left-0 right-0">
                            <Progress value={(duration / maxDuration) * 100} className="h-1 rounded-none" />
                        </div>
                    )}
                </div>

                {/* Controls */}
                <div className="flex items-center justify-center gap-3">
                    {!isRecording && !isPreviewing && (
                        <Button onClick={startRecording} className="gap-2">
                            <Video className="h-4 w-4" />
                            Start Recording
                        </Button>
                    )}

                    {isRecording && (
                        <>
                            <Button
                                variant="outline"
                                size="icon"
                                onClick={toggleMute}
                            >
                                {isMuted ? <MicOff className="h-4 w-4" /> : <Mic className="h-4 w-4" />}
                            </Button>
                            <Button
                                variant="destructive"
                                onClick={stopRecording}
                                className="gap-2"
                            >
                                <StopCircle className="h-4 w-4" />
                                Stop Recording
                            </Button>
                        </>
                    )}

                    {isPreviewing && (
                        <>
                            <Button
                                variant="outline"
                                onClick={discardRecording}
                                className="gap-2"
                            >
                                <Trash2 className="h-4 w-4" />
                                Discard
                            </Button>
                            <Button
                                variant="outline"
                                onClick={downloadRecording}
                                className="gap-2"
                            >
                                <Download className="h-4 w-4" />
                                Download
                            </Button>
                            <Button
                                onClick={uploadRecording}
                                disabled={isUploading}
                                className="gap-2"
                            >
                                {isUploading ? (
                                    <Loader2 className="h-4 w-4 animate-spin" />
                                ) : (
                                    <Upload className="h-4 w-4" />
                                )}
                                {isUploading ? 'Uploading...' : 'Use Recording'}
                            </Button>
                        </>
                    )}
                </div>

                {/* Duration Info */}
                {isRecording && (
                    <p className="text-center text-sm text-muted-foreground">
                        Max duration: {formatTime(maxDuration)}
                    </p>
                )}
            </CardContent>
        </Card>
    )
}

export default VideoRecorder

"use client"

import * as React from 'react'
import { Button } from '@/components/ui/button'
import { Card } from '@/components/ui/card'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Bot, X, Send, Minimize2, User, Phone, Mail } from 'lucide-react'
import api from '@/lib/api' // Ensure this axios instance handles auth headers automatically if present

// Basic type for chat messages
interface Message {
    role: 'user' | 'model'
    text: string
}

export default function ChatWidget() {
    const [isOpen, setIsOpen] = React.useState(false)
    const [messages, setMessages] = React.useState<Message[]>([
        { role: 'model', text: 'Hi! I am TechWell Bot. How can I help you today?' }
    ])
    const [inputText, setInputText] = React.useState('')
    const [isThinking, setIsThinking] = React.useState(false)

    // Guest / Lead State
    const [isGuest, setIsGuest] = React.useState(true) // Default to guest, verified by checking token/localstorage usually
    const [hasProvidedDetails, setHasProvidedDetails] = React.useState(false)
    const [leadForm, setLeadForm] = React.useState({
        name: '',
        email: '',
        phone: ''
    })

    const scrollRef = React.useRef<HTMLDivElement>(null)

    // Check if user is logged in
    React.useEffect(() => {
        // Simple check: if token exists in localStorage, assume user
        const token = localStorage.getItem('token')
        if (token) {
            setIsGuest(false)
            setHasProvidedDetails(true) // Users don't need to fill form
        }
    }, [])

    // Auto-scroll
    React.useEffect(() => {
        if (scrollRef.current) {
