"use client"

import { useState, useEffect } from "react"
import { useParams, useRouter } from "next/navigation"
import api from "@/lib/api"
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import { Loader2, Mail, Phone, Calendar, Star, MoreVertical, Download } from "lucide-react"

interface ColumnConfig {
    title: string
    color: string
}

const COLUMNS: Record<string, ColumnConfig> = {
    APPLIED: { title: "Applied", color: "bg-gray-100" },
    VIEWED: { title: "Viewed", color: "bg-blue-50" }, // Added Viewed
    SCREENED: { title: "Screened", color: "bg-indigo-50" },
    SHORTLISTED: { title: "Shortlisted", color: "bg-purple-50" },
    INTERVIEW_SCHEDULED: { title: "Interview", color: "bg-pink-50" },
    SELECTED: { title: "Selected", color: "bg-green-50" },
    APPOINTED: { title: "Hired", color: "bg-emerald-100 border-emerald-200" }, // Renamed Hired in UI
    REJECTED: { title: "Rejected", color: "bg-red-50" }
}

import type { DropResult } from "@hello-pangea/dnd"

export default function ATSPipelinePage() {
    const { id: jobId } = useParams()
    const router = useRouter()
    interface Job {
        id: string
        title: string
    }

    interface Application {
        id: string
        status: string
        source: string
        atsScore: number
        externalName?: string
        externalEmail?: string
        createdAt: string
        applicant?: {
            name: string
            email: string
            avatar?: string
        }
    }

    const [applications, setApplications] = useState<Application[]>([])
    const [job, setJob] = useState<Job | null>(null)
    const [isLoading, setIsLoading] = useState(true)

    useEffect(() => {
        fetchData()
    }, [jobId])

    const fetchData = async () => {
        try {
            const [jobRes, appsRes] = await Promise.all([
                api.get(`/jobs/${jobId}`),
                api.get(`/ats/applications/${jobId}`)
            ])
            setJob(jobRes.data)
            setApplications(appsRes.data)
        } catch (error) {
            console.error(error)
        } finally {
            setIsLoading(false)
        }
    }

    const handleExport = async () => {
        try {
            const res = await api.get(`/ats/export/${jobId}`, { responseType: 'blob' })
            const url = window.URL.createObjectURL(new Blob([res.data]))
            const link = document.createElement('a')
            link.href = url
            link.setAttribute('download', `applicants-${jobId}.csv`)
            document.body.appendChild(link)
            link.click()
            link.remove()
        } catch (error) {
            console.error("Failed to export", error)
            alert("Failed to export data")
        }
    }

    const onDragEnd = async (result: DropResult) => {
        if (!result.destination) return

        const { draggableId, destination } = result
        const newStatus = destination.droppableId

        // Optimistic UI update
        const updatedApps = applications.map(app =>
            app.id === draggableId ? { ...app, status: newStatus } : app
        )
        setApplications(updatedApps)

        // API Call
        try {
            await api.patch(`/ats/status/${draggableId}`, { status: newStatus })
        } catch (error) {
            console.error("Failed to update status", error)
            fetchData() // Revert on error
        }
    }

    if (isLoading) return <div className="flex justify-center p-8"><Loader2 className="animate-spin" /></div>

    // Group apps by status
    const columns = Object.keys(COLUMNS).reduce((acc: Record<string, Application[]>, status) => {
        acc[status] = applications.filter(app => app.status === status)
        return acc
    }, {})

    return (
        <div className="container py-6 h-[calc(100vh-60px)] flex flex-col">
            <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-6 gap-4">
                <div>
                    <h1 className="text-2xl font-bold">{job?.title} Pipeline</h1>
                    <div className="flex gap-2 text-sm text-muted-foreground mt-1">
                        <Badge variant="outline">{applications.length} Candidates</Badge>
                        <Badge variant="outline" className="bg-blue-50 text-blue-700">
                            {applications.filter(a => a.source === 'INTERNAL').length} Internal
                        </Badge>
                    </div>
                </div>
                <div className="flex flex-wrap gap-2">
                    <Button variant="outline" size="sm" onClick={() => handleExport()}>
                        <Download className="mr-2 h-4 w-4" /> Export
                    </Button>
                    <Button variant="ghost" size="sm" onClick={() => router.push(`/employer/jobs/edit/${jobId}`)}>Edit Job</Button>
                    <Button size="sm" onClick={() => router.push(`/employer/dashboard/ats/add-candidate`)}>Add Candidate</Button>
                </div>
            </div>

            <DragDropContext onDragEnd={onDragEnd}>
                <div className="flex-1 overflow-x-auto pb-4">
                    <div className="flex gap-4 min-w-[1200px] h-full">
                        {(Object.entries(COLUMNS) as [string, ColumnConfig][]).map(([status, config]) => (
                            <div key={status} className={`flex-1 min-w-[280px] rounded-lg p-3 flex flex-col ${config.color}`}>
                                <h3 className="font-semibold mb-3 flex justify-between items-center">
                                    {config.title}
                                    <Badge variant="secondary" className="bg-white/50">{columns[status]?.length || 0}</Badge>
                                </h3>

                                <Droppable droppableId={status}>
                                    {(provided) => (
                                        <div
                                            ref={provided.innerRef}
                                            {...provided.droppableProps}
                                            className="flex-1 overflow-y-auto space-y-3 min-h-[100px]"
                                        >
                                            {columns[status]?.map((app, index: number) => (
                                                <Draggable key={app.id} draggableId={app.id} index={index}>
                                                    {(provided) => (
                                                        <Card
                                                            ref={provided.innerRef}
                                                            {...provided.draggableProps}
                                                            {...provided.dragHandleProps}
                                                            className="shadow-sm hover:shadow-md cursor-grab active:cursor-grabbing border-none"
                                                            onClick={() => router.push(`/employer/dashboard/ats/candidate/${app.id}`)}
                                                        >
                                                            <CardContent className="p-3">
                                                                <div className="flex items-start justify-between mb-2">
                                                                    <div className="flex items-center gap-2">
                                                                        {app.source === 'INTERNAL' && (
                                                                            <Badge variant="default" className="bg-blue-600 text-[10px] h-5 px-1">TechWell</Badge>
                                                                        )}
                                                                        {app.atsScore > 0 && (
                                                                            <Badge variant="outline" className={`text-[10px] h-5 px-1 ${app.atsScore >= 80 ? 'text-green-600 border-green-200' : 'text-orange-500'}`}>
                                                                                {app.atsScore}% Match
                                                                            </Badge>
                                                                        )}
                                                                    </div>
                                                                </div>

                                                                <div className="flex items-center gap-3 mb-3">
                                                                    <Avatar className="h-8 w-8">
                                                                        <AvatarImage src={app.applicant?.avatar} />
                                                                        <AvatarFallback>{(app.applicant?.name || app.externalName || 'U')[0]}</AvatarFallback>
                                                                    </Avatar>
                                                                    <div>
                                                                        <p className="font-medium text-sm line-clamp-1">{app.applicant?.name || app.externalName}</p>
                                                                        <p className="text-xs text-muted-foreground line-clamp-1">{app.applicant?.email || app.externalEmail}</p>
                                                                    </div>
                                                                </div>

                                                                <div className="flex justify-between items-center mt-2 border-t pt-2">
                                                                    <div className="text-xs text-muted-foreground flex items-center gap-1">
                                                                        <Calendar className="w-3 h-3" />
                                                                        {new Date(app.createdAt).toLocaleDateString()}
                                                                    </div>
                                                                    <Button variant="ghost" size="icon" className="h-6 w-6">
                                                                        <MoreVertical className="w-3 h-3" />
                                                                    </Button>
                                                                </div>
                                                            </CardContent>
                                                        </Card>
                                                    )}
                                                </Draggable>
                                            ))}
                                            {provided.placeholder}
                                        </div>
                                    )}
                                </Droppable>
                            </div>
                        ))}
                    </div>
                </div>
            </DragDropContext>
        </div>
    )
}

"use client"

import { useState, useEffect } from "react"
import { useRouter, useSearchParams } from "next/navigation"
import api from "@/lib/api"
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Calendar } from "@/components/ui/calendar"
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover"
import { format } from "date-fns"
import { Calendar as CalendarIcon, Clock, Link as LinkIcon, User } from "lucide-react"
import { cn } from "@/lib/utils"

export default function ScheduleInterviewPage() {
    const router = useRouter()
    const searchParams = useSearchParams()
    const appId = searchParams.get('appId')

    const [application, setApplication] = useState<any>(null)
    const [isLoading, setIsLoading] = useState(true)
    const [isSubmitting, setIsSubmitting] = useState(false)

    const [formData, setFormData] = useState({
        roundName: '',
        type: 'TECHNICAL',
        date: undefined as Date | undefined,
        time: '10:00',
        duration: '30',
        meetingLink: '',
        interviewerId: ''
    })

    useEffect(() => {
        if (appId) fetchApplication()
    }, [appId])

    const fetchApplication = async () => {
        try {
            const res = await api.get(`/ats/applications/detail/${appId}`)
            setApplication(res.data)
            // Default round name based on existing interviews count? 
            // For now simple default
            setFormData(prev => ({ ...prev, roundName: 'Technical Round 1' }))
        } catch (error) {
            console.error(error)
            alert("Failed to load application details")
        } finally {
            setIsLoading(false)
        }
    }

    const handleSubmit = async () => {
        if (!formData.date || !formData.time || !formData.roundName) {
            alert("Please fill all required fields")
            return
        }

        setIsSubmitting(true)
        try {
            // Combine Date and Time
            const scheduledAt = new Date(formData.date)
            const [hours, minutes] = formData.time.split(':')
            scheduledAt.setHours(parseInt(hours), parseInt(minutes))

            await api.post('/ats/interviews', {
                applicationId: appId,
                roundName: formData.roundName,
                type: formData.type,
                scheduledAt: scheduledAt.toISOString(),
                duration: formData.duration,
                meetingLink: formData.meetingLink
            })

            alert("Interview Scheduled Successfully!")
            router.push(`/employer/dashboard/ats/candidate/${appId}`)
        } catch (error: any) {
            alert(error.response?.data?.error || "Failed to schedule interview")
        } finally {
            setIsSubmitting(false)
        }
    }

    if (!appId) return <div className="p-8">Missing Application ID</div>
    if (isLoading) return <div className="p-8">Loading...</div>

    const candidateName = application?.applicant?.name || application?.externalName
    const jobTitle = application?.job?.title

    return (
        <div className="container py-8 max-w-2xl">
            <Card>
                <CardHeader>
                    <CardTitle>Schedule Interview</CardTitle>
                    <CardDescription>
                        For <strong>{candidateName}</strong> - {jobTitle}
                    </CardDescription>
                </CardHeader>
                <CardContent className="space-y-6">
                    <div className="space-y-2">
                        <Label>Round Name</Label>
                        <Input
                            value={formData.roundName}
                            onChange={e => setFormData({ ...formData, roundName: e.target.value })}
                            placeholder="e.g. Technical Round 1"
                        />
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                        <div className="space-y-2">
                            <Label>Round Type</Label>
                            <Select
                                value={formData.type}
                                onValueChange={val => setFormData({ ...formData, type: val })}
                            >
                                <SelectTrigger>
                                    <SelectValue />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="SCREENING">Screening</SelectItem>
                                    <SelectItem value="TECHNICAL">Technical</SelectItem>
                                    <SelectItem value="MANAGERIAL">Managerial</SelectItem>
                                    <SelectItem value="HR">HR</SelectItem>
                                    <SelectItem value="FINAL">Final</SelectItem>
                                </SelectContent>
                            </Select>
                        </div>
                        <div className="space-y-2">
                            <Label>Duration (Minutes)</Label>
                            <Select
                                value={formData.duration}
                                onValueChange={val => setFormData({ ...formData, duration: val })}
                            >
                                <SelectTrigger>
                                    <SelectValue />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="15">15 mins</SelectItem>
                                    <SelectItem value="30">30 mins</SelectItem>
                                    <SelectItem value="45">45 mins</SelectItem>
                                    <SelectItem value="60">1 Hour</SelectItem>
                                </SelectContent>
                            </Select>
                        </div>
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                        <div className="space-y-2 flex flex-col">
                            <Label className="mb-2">Date</Label>
                            <Popover>
                                <PopoverTrigger asChild>
                                    <Button
                                        variant={"outline"}
                                        className={cn(
                                            "w-full justify-start text-left font-normal",
                                            !formData.date && "text-muted-foreground"
                                        )}
                                    >
                                        <CalendarIcon className="mr-2 h-4 w-4" />
                                        {formData.date ? format(formData.date, "PPP") : <span>Pick a date</span>}
                                    </Button>
                                </PopoverTrigger>
                                <PopoverContent className="w-auto p-0">
                                    <Calendar
                                        mode="single"
                                        selected={formData.date}
                                        onSelect={date => setFormData({ ...formData, date })}
                                        initialFocus
                                        disabled={(date) => date < new Date()}
                                    />
                                </PopoverContent>
                            </Popover>
                        </div>
                        <div className="space-y-2">
                            <Label>Time</Label>
                            <div className="relative">
                                <Clock className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                                <Input
                                    type="time"
                                    className="pl-9"
                                    value={formData.time}
                                    onChange={e => setFormData({ ...formData, time: e.target.value })}
                                />
                            </div>
                        </div>
                    </div>

                    <div className="space-y-2">
                        <Label>Meeting Link (Google Meet / Zoom)</Label>
                        <div className="relative">
                            <LinkIcon className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                            <Input
                                className="pl-9"
                                placeholder="https://meet.google.com/..."
                                value={formData.meetingLink}
                                onChange={e => setFormData({ ...formData, meetingLink: e.target.value })}
                            />
                        </div>
                        <p className="text-xs text-muted-foreground">The candidate will receive this link via email.</p>
                    </div>

                    <div className="pt-4 flex justify-end gap-2">
                        <Button variant="outline" onClick={() => router.back()}>Cancel</Button>
                        <Button onClick={handleSubmit} disabled={isSubmitting}>
                            {isSubmitting ? 'Scheduling...' : 'Schedule Interview'}
                        </Button>
                    </div>
                </CardContent>
            </Card>
        </div>
    )
}

"use client"

import * as React from 'react'
import { useRouter, useParams } from 'next/navigation'
import { useAuth } from '@/lib/auth-context'
import { interviewApi } from '@/lib/api'
import { Button } from '@/components/ui/button'
import { Card, CardContent } from '@/components/ui/card'
import {
    Video,
    VideoOff,
    Mic,
    MicOff,
    Phone,
    ChevronRight,
    MessageSquare,
    Clock,
    User,
    Bot,
    Loader2,
    CheckCircle2,
    ArrowLeft,
    Send,
    Briefcase
} from 'lucide-react'

// Speech Recognition Types
interface ISpeechRecognitionEvent extends Event {
    resultIndex: number
    results: {
        [key: number]: {
            [key: number]: {
                transcript: string
            }
            length: number
        }
        length: number
    }
}

interface ISpeechRecognitionErrorEvent extends Event {
    error: string
}

interface ISpeechRecognition extends EventTarget {
    continuous: boolean
    interimResults: boolean
    onresult: (event: ISpeechRecognitionEvent) => void
    onerror: (event: ISpeechRecognitionErrorEvent) => void
    onend: () => void
    start: () => void
    stop: () => void
}

declare global {
    interface Window {
        SpeechRecognition: new () => ISpeechRecognition
        webkitSpeechRecognition: new () => ISpeechRecognition
    }
}

interface Message {
    id: string
    role: 'interviewer' | 'user'
    content: string
    timestamp: Date
}

interface Question {
    id: string
    question: string
    type: string
    avatarRole: string // 'Technical', 'HR', etc.
}

interface AvatarConfig {
    name: string
    role: string
    color: string
    icon: React.ElementType
}

const AVATARS: Record<string, AvatarConfig> = {
    'tech-1': { name: "Alex Chen", role: "Technical Lead", color: "from-blue-500/20 to-cyan-500/20", icon: Bot },
    'tech-2': { name: "Sarah Johnson", role: "Senior Engineer", color: "from-indigo-500/20 to-blue-500/20", icon: Bot },
    'hr-1': { name: "Emma Williams", role: "HR Manager", color: "from-purple-500/20 to-pink-500/20", icon: Briefcase },
    'hr-2': { name: "David Smith", role: "Talent Acquisition", color: "from-slate-500/20 to-gray-500/20", icon: Briefcase },
    // Fallbacks
    'Technical': { name: "AI Interviewer", role: "Technical", color: "from-blue-500/20 to-cyan-500/20", icon: Bot },
    'HR': { name: "AI Recruiter", role: "HR", color: "from-purple-500/20 to-pink-500/20", icon: Briefcase }
}

export default function InterviewRoomPage() {
    const router = useRouter()
    const params = useParams()
    const { isAuthenticated, isLoading: authLoading } = useAuth()

    const videoRef = React.useRef<HTMLVideoElement>(null)
    const [stream, setStream] = React.useState<MediaStream | null>(null)
    const [isVideoOn, setIsVideoOn] = React.useState(true)
    const [isMicOn, setIsMicOn] = React.useState(true)

    // AI State
    const [currentQuestion, setCurrentQuestion] = React.useState<Question | null>(null)
    const [questionCount, setQuestionCount] = React.useState(0)

    const [messages, setMessages] = React.useState<Message[]>([])
    const [userResponse, setUserResponse] = React.useState('')
    const [isThinking, setIsThinking] = React.useState(false)
    const [isAIProcessing, setIsAIProcessing] = React.useState(false)

    interface Interview {
        id: string
        role: string
        company?: string
        technology?: string
        difficulty?: string
        duration?: number
        panelCount?: number
        selectedAvatars?: string[]
    }

    const [interview, setInterview] = React.useState<Interview | null>(null)
    const [timeLeft, setTimeLeft] = React.useState(0) // Seconds
    const [isCompleted, setIsCompleted] = React.useState(false)

    React.useEffect(() => {
        if (!authLoading && !isAuthenticated) {
            router.push('/login')
        }
    }, [authLoading, isAuthenticated, router])

    // Load Interview & Init Timer
    React.useEffect(() => {
        const fetchInterview = async () => {
            try {
                const res = await interviewApi.getById(params.id as string)
                setInterview(res.data.interview)
                // Initialize timer (default 30 mins if missing)
                if (res.data.interview.duration) {
                    setTimeLeft(res.data.interview.duration * 60)
                } else {
                    setTimeLeft(30 * 60)
                }
            } catch (error) {
                console.error('Failed to fetch interview:', error)
            }
        }
        if (params.id) {
            fetchInterview()
        }
    }, [params.id])

    // Countdown Timer
    React.useEffect(() => {
        if (!isCompleted && timeLeft > 0) {
            const timer = setInterval(() => {
                setTimeLeft(prev => {
                    if (prev <= 1) {
                        clearInterval(timer)
                        handleEndInterview() // Auto-end
                        return 0
                    }
                    return prev - 1
                })
            }, 1000)
            return () => clearInterval(timer)
        }
    }, [isCompleted, timeLeft])

    // Initialize camera stream
    React.useEffect(() => {
        const initCamera = async () => {
            try {
                const mediaStream = await navigator.mediaDevices.getUserMedia({
                    video: true,
                    audio: true
                })
                setStream(mediaStream)
                if (videoRef.current) {
                    videoRef.current.srcObject = mediaStream
                }
            } catch (error) {
                console.error('Failed to access camera:', error)
            }
        }
        initCamera()

        return () => {
            if (stream) {
                stream.getTracks().forEach(track => track.stop())
            }
        }
    }, [])

    // Initial Question Fetch
    React.useEffect(() => {
        const startInterview = async () => {
            if (messages.length === 0 && !isCompleted && !currentQuestion) {
                await fetchNextQuestion();
            }
        }
        startInterview();
    }, [messages.length, isCompleted])

    const handleEndInterview = async () => {
        try {
            await interviewApi.complete(params.id as string, { score: Math.floor(Math.random() * 20) + 80 })
        } catch (error) {
            console.error('Failed to complete interview:', error)
        }
        if (stream) {
            stream.getTracks().forEach(track => track.stop())
        }
        router.push(`/interviews/${params.id}/report`)
    }

    // Timer
    React.useEffect(() => {
        if (!isCompleted && timeLeft > 0) {
            const timer = setInterval(() => {
                setTimeLeft(prev => {
                    if (prev <= 1) {
                        clearInterval(timer)
                        handleEndInterview() // Auto-end
                        return 0
                    }
                    return prev - 1
                })
            }, 1000)
            return () => clearInterval(timer)
        }
    }, [isCompleted, timeLeft])

    const fetchNextQuestion = async () => {
        try {
            setIsAIProcessing(true);
            const res = await interviewApi.nextQuestion(params.id as string);
            const questionData = res.data.question;

            setCurrentQuestion({
                id: questionData.id || 'temp',
                question: questionData.question,
                type: questionData.type,
                avatarRole: questionData.avatarId?.includes('hr') ? 'HR' : 'Technical' // Simple mapping based on mock ID
            });

            setQuestionCount(prev => prev + 1);

            // Add to chat
            setMessages(prev => [...prev, {
                id: Date.now().toString(),
                role: 'interviewer',
                content: questionData.question,
                timestamp: new Date()
            }]);

            // Speak the question
            speakText(questionData.question);
        } catch (error) {
            console.error("Failed to fetch next question:", error);
            // Fallback for demo if backend not ready
            if (!currentQuestion) {
                setMessages(prev => [...prev, {
                    id: 'error',
                    role: 'interviewer',
                    content: "Connecting to AI Interviewer... (Please ensure backend is restarted)",
                    timestamp: new Date()
                }]);
            }
        } finally {
            setIsAIProcessing(false);
        }
    }

    const formatTime = (seconds: number) => {
        const mins = Math.floor(seconds / 60)
        const secs = seconds % 60
        return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`
    }

    const [isWaitingNext, setIsWaitingNext] = React.useState(false)
    const [nextQuestionTimer, setNextQuestionTimer] = React.useState(10)
    const [isRecording, setIsRecording] = React.useState(false)
    const recognitionRef = React.useRef<ISpeechRecognition | null>(null)
    const speechTimeoutRef = React.useRef<NodeJS.Timeout | null>(null)

    // Handle Speech Recognition
    React.useEffect(() => {
        const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition
        if (SpeechRecognition) {
            recognitionRef.current = new SpeechRecognition()
            if (recognitionRef.current) {
                recognitionRef.current.continuous = true
                recognitionRef.current.interimResults = true

                recognitionRef.current.onresult = (event: ISpeechRecognitionEvent) => {
                    let transcript = ''
                    for (let i = event.resultIndex; i < event.results.length; i++) {
                        transcript += event.results[i][0].transcript
                    }
                    setUserResponse(transcript)
                }

                recognitionRef.current.onerror = (event: ISpeechRecognitionErrorEvent) => {
                    console.error('Speech recognition error:', event.error)
                    setIsRecording(false)
                }

                recognitionRef.current.onend = () => {
                    setIsRecording(false)
                }
            }
        }
    }, [])

    const toggleRecording = () => {
        if (isRecording) {
            recognitionRef.current?.stop()
        } else {
            setUserResponse('')
            recognitionRef.current?.start()
            setIsRecording(true)
        }
    }

    const speakText = (text: string) => {
        if (typeof window !== 'undefined' && window.speechSynthesis) {
            // Cancel any ongoing speech
            window.speechSynthesis.cancel()

            const utterance = new SpeechSynthesisUtterance(text)
            utterance.rate = 0.95
            utterance.pitch = 1

            // Get voices and find a good one (prefer female for HR, male for Tech maybe)
            const voices = window.speechSynthesis.getVoices()
            const preferredVoice = voices.find(v => v.name.includes('Google') || v.name.includes('Premium'))
            if (preferredVoice) utterance.voice = preferredVoice

            utterance.onend = () => {
                // Start 10s timeout after speaking is done
                startResponseTimer()
                // Auto-start recording
                toggleRecording()
            }

            window.speechSynthesis.speak(utterance)
        }
    }

    const startResponseTimer = () => {
        setNextQuestionTimer(10)
        if (speechTimeoutRef.current) clearInterval(speechTimeoutRef.current)

        speechTimeoutRef.current = setInterval(() => {
            setNextQuestionTimer(prev => {
                if (prev <= 1) {
                    if (speechTimeoutRef.current) clearInterval(speechTimeoutRef.current)
                    handleAutoSubmit()
                    return 0
                }
                return prev - 1
            })
        }, 1000)
    }

    const handleAutoSubmit = () => {
        if (isRecording) recognitionRef.current?.stop()
        handleSendResponse(true)
    }

    // Re-attach stream when video toggles back on
    React.useEffect(() => {
        if (isVideoOn && videoRef.current && stream) {
            videoRef.current.srcObject = stream
        }
    }, [isVideoOn, stream])

    // Gap Timer
    React.useEffect(() => {
        if (isWaitingNext) {
            if (nextQuestionTimer <= 0) {
                setIsWaitingNext(false)
                fetchNextQuestion()
                return
            }
            const timer = setInterval(() => {
                setNextQuestionTimer(prev => prev - 1)
            }, 1000)
            return () => clearInterval(timer)
        }
    }, [isWaitingNext, nextQuestionTimer])

    // ... existing initialization ...

    const handleSendResponse = async (isAuto = false) => {
        if (!currentQuestion) return
        if (!userResponse.trim() && !isAuto) return // Don't allow manual empty submit

        const finalResponse = userResponse.trim() || "(No verbal response recorded)";

        // Add user message if there was a response or if it's auto-submit
        const userMessage: Message = {
            id: Date.now().toString(),
            role: 'user',
            content: finalResponse,
            timestamp: new Date()
        }
        setMessages(prev => [...prev, userMessage])
        setUserResponse('')
        setIsThinking(true)

        // Clear auto-submit timer
        if (speechTimeoutRef.current) clearInterval(speechTimeoutRef.current)
        if (isRecording) recognitionRef.current?.stop()

        // Submit to AI
        try {
            await interviewApi.submitResponse(params.id as string, {
                questionId: currentQuestion.id,
                answer: finalResponse
            });

            // Decide to continue or end
            if (questionCount >= 5) {
                setIsCompleted(true);
                setMessages(prev => [...prev, {
                    id: Date.now().toString(),
                    role: 'interviewer',
                    content: "Thank you! The interview is complete. Generating your feedback report...",
                    timestamp: new Date()
                }]);
            } else {
                setNextQuestionTimer(10)
                setIsWaitingNext(true)
            }
        } catch (error) {
            console.error("Error submitting response:", error);
            // Even on error, try to move forward if it was an auto-submit to prevent hang
            if (isAuto && questionCount < 5) {
                setNextQuestionTimer(10)
                setIsWaitingNext(true)
            }
        } finally {
            setIsThinking(false);
        }
    }



    if (authLoading || !isAuthenticated) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <Loader2 className="h-8 w-8 animate-spin text-primary" />
            </div>
        )
    }

    const getActiveAvatar = () => {
        // 1. If question has specific avatar role/id, use it
        if (currentQuestion?.avatarRole && AVATARS[currentQuestion.avatarRole]) {
            return AVATARS[currentQuestion.avatarRole]
        }
        // 2. Use interview selected avatar
        const primaryAvatarId = interview?.selectedAvatars?.[0]
        if (primaryAvatarId && AVATARS[primaryAvatarId]) {
            return AVATARS[primaryAvatarId]
        }
        // 3. Fallback
        return AVATARS['tech-1'] || AVATARS['Technical']
    }

    const activeAvatar = getActiveAvatar()

    const AvatarIcon = activeAvatar.icon;

    return (
        <div className="min-h-screen bg-background">
            {/* Header Bar */}
            <div className="h-14 bg-muted/50 border-b flex items-center justify-between px-4 backdrop-blur-md sticky top-0 z-20">
                <div className="flex items-center gap-4">
                    <Button variant="ghost" size="sm" onClick={() => router.push('/interviews')}>
                        <ArrowLeft className="h-4 w-4 mr-2" />
                        Exit
                    </Button>
                    <div className="h-6 w-px bg-border" />
                    <div className="flex flex-col">
                        <div className="flex items-center gap-2 text-sm">
                            <Bot className="h-4 w-4 text-primary" />
                            <span className="font-bold">{interview?.role || 'Interview'}</span>
                            {interview?.technology && (
                                <span className="bg-primary/10 text-primary text-[10px] px-2 py-0.5 rounded-full">
                                    {interview.technology}
                                </span>
                            )}
                        </div>
                        <p className="text-[10px] text-muted-foreground uppercase tracking-wider font-semibold">
                            {interview?.company || 'Mock Interview'}  {interview?.difficulty} Level
                        </p>
                    </div>
                </div>
                <div className="flex items-center gap-6">
                    <div className="flex items-center gap-2 text-sm bg-background/50 px-3 py-1 rounded-full border">
                        <Clock className="h-4 w-4 text-primary" />
                        <span className="font-mono font-bold">{formatTime(timeLeft)}</span>
                    </div>
                    <div className="flex items-center gap-2 text-sm">
                        <span className="text-muted-foreground">Progress:</span>
                        <div className="flex gap-1">
                            {[1, 2, 3, 4, 5].map(step => (
                                <div
                                    key={step}
                                    className={`h-1.5 w-6 rounded-full ${step <= questionCount ? 'bg-primary' : 'bg-muted'}`}
                                />
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            <div className="flex h-[calc(100vh-3.5rem)] overflow-hidden">
                {/* Left Sidebar: Panel visualization */}
                <div className="w-64 border-r bg-muted/30 flex flex-col p-4 gap-4 hidden md:flex">
                    <div className="space-y-1">
                        <h3 className="text-xs font-bold text-muted-foreground uppercase tracking-widest pl-2">Interviewer Panel</h3>
                        <p className="text-[10px] text-muted-foreground pl-2">{interview?.panelCount || 1} Experts Conducting</p>
                    </div>

                    <div className="space-y-2">
                        {/* List only avatars selected for this panel */}
                        {Object.entries(AVATARS)
                            .filter(([id]) => interview?.selectedAvatars?.includes(id))
                            .map(([id, avatar]) => {
                                const isActive = activeAvatar?.name === avatar.name;
                                return (
                                    <div
                                        key={id}
                                        className={`flex items-center gap-3 p-3 rounded-xl transition-all duration-300 border-2 ${isActive
                                            ? 'bg-primary/10 border-primary shadow-lg shadow-primary/10 scale-105'
                                            : 'bg-white/5 border-transparent opacity-60 grayscale hover:grayscale-0 hover:opacity-100 hover:bg-white/10'
                                            }`}
                                    >
                                        <div className={`p-2 rounded-lg bg-gradient-to-br ${avatar.color}`}>
                                            <avatar.icon className={`h-5 w-5 ${isActive ? 'text-primary animate-pulse' : 'text-muted-foreground'}`} />
                                        </div>
                                        <div className="flex-1 min-w-0 text-left">
                                            <div className="flex items-center gap-1.5">
                                                <p className={`text-sm font-bold truncate ${isActive ? 'text-foreground' : 'text-muted-foreground'}`}>{avatar.name}</p>
                                                {isActive && <div className="h-1.5 w-1.5 rounded-full bg-primary animate-ping" />}
                                            </div>
                                            <p className="text-[10px] text-muted-foreground uppercase font-bold tracking-wider truncate">{avatar.role}</p>
                                        </div>
                                        {isActive && (
                                            <div className="px-2 py-0.5 rounded-full bg-primary/20 text-primary text-[8px] font-black uppercase tracking-tighter">
                                                Active
                                            </div>
                                        )}
                                    </div>
                                );
                            })}
                    </div>

                    <div className="mt-auto p-4 bg-primary/5 rounded-2xl border border-primary/10">
                        <div className="flex items-center gap-2 mb-2">
                            <CheckCircle2 className="h-4 w-4 text-primary" />
                            <span className="text-xs font-bold">Live Mentoring</span>
                        </div>
                        <p className="text-[10px] text-muted-foreground leading-relaxed">
                            Interviewer is analyzing your STAR method utilization and technical depth in real-time.
                        </p>
                    </div>
                </div>

                {/* Video Section */}
                <div className="flex-1 p-4 flex flex-col gap-4 bg-muted/10">
                    {/* Main Video Grid */}
                    <div className="flex-1 grid grid-cols-1 md:grid-cols-2 gap-4 h-full min-h-0">
                        {/* AI Interviewer - Dynamic Avatar */}
                        <Card className={`relative overflow-hidden border-2 transition-all duration-500 flex flex-col ${isAIProcessing ? 'border-primary/50' : 'border-transparent shadow-xl'}`}>
                            <CardContent className={`h-full flex items-center justify-center p-0 bg-gradient-to-br ${activeAvatar.color}`}>
                                <div className="text-center">
                                    <div className="h-32 w-32 rounded-full bg-background/50 backdrop-blur-md flex items-center justify-center mx-auto mb-6 shadow-2xl animate-in zoom-in duration-500">
                                        {activeAvatar && <activeAvatar.icon className="h-16 w-16 text-primary" />}
                                    </div>
                                    <h2 className="font-bold text-2xl tracking-tight">{activeAvatar.name}</h2>
                                    <p className="text-sm font-medium text-muted-foreground bg-background/50 px-3 py-1 rounded-full inline-block mt-2 border">
                                        {activeAvatar.role}
                                    </p>
                                </div>

                                {isAIProcessing && (
                                    <div className="absolute inset-0 flex items-center justify-center bg-background/60 backdrop-blur-md z-30">
                                        <div className="flex flex-col items-center gap-4">
                                            <Loader2 className="h-10 w-10 animate-spin text-primary" />
                                            <span className="text-sm font-bold uppercase tracking-widest text-primary">Generating Insights...</span>
                                        </div>
                                    </div>
                                )}

                                {isWaitingNext && (
                                    <div className="absolute inset-0 flex items-center justify-center bg-background/80 backdrop-blur-md z-40">
                                        <div className="flex flex-col items-center gap-4 p-8 rounded-3xl bg-background shadow-2xl border text-center max-w-xs">
                                            <div className="h-16 w-16 rounded-full border-4 border-primary/20 flex items-center justify-center relative">
                                                <div className="absolute inset-0 border-4 border-primary rounded-full border-t-transparent animate-spin-slow" />
                                                <span className="font-mono font-bold text-2xl">{nextQuestionTimer}</span>
                                            </div>
                                            <div className="space-y-1">
                                                <p className="font-bold">Excellent Response!</p>
                                                <p className="text-xs text-muted-foreground">Preparing follow-up question...</p>
                                            </div>
                                            <Button size="sm" variant="outline" className="w-full rounded-full" onClick={() => { setIsWaitingNext(false); fetchNextQuestion() }}>
                                                Skip to Next
                                            </Button>
                                        </div>
                                    </div>
                                )}

                                {!isAIProcessing && currentQuestion && !isCompleted && (
                                    <div className="absolute top-4 right-4 flex items-center gap-2 bg-green-500/10 border border-green-500/20 px-3 py-1.5 rounded-full z-10 backdrop-blur-md">
                                        <div className="h-2 w-2 rounded-full bg-green-500 animate-pulse" />
                                        <span className="text-[10px] font-bold text-green-600 uppercase">Live Discussion</span>
                                    </div>
                                )}
                            </CardContent>
                        </Card>

                        {/* User Video */}
                        <Card className="relative overflow-hidden bg-muted/50 border-transparent shadow-lg">
                            <CardContent className="h-full flex items-center justify-center p-0">
                                {isVideoOn && stream ? (
                                    <video
                                        ref={videoRef}
                                        autoPlay
                                        muted
                                        playsInline
                                        className="w-full h-full object-cover scale-x-[-1]"
                                    />
                                ) : (
                                    <div className="text-center p-8">
                                        <div className="h-28 w-28 rounded-full bg-muted/50 flex items-center justify-center mx-auto mb-4 border-2 border-dashed border-muted">
                                            {isVideoOn ? <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" /> : <VideoOff className="h-8 w-8 text-muted-foreground" />}
                                        </div>
                                        <p className="font-bold">You</p>
                                        <p className="text-xs text-muted-foreground">{isVideoOn ? 'Camera loading...' : 'Camera is disabled'}</p>
                                    </div>
                                )}
                                <div className="absolute bottom-4 left-4 flex items-center gap-3 bg-black/60 backdrop-blur-md px-4 py-2 rounded-2xl text-white">
                                    {isMicOn ? (
                                        <div className="flex items-center gap-2">
                                            <div className="flex gap-0.5 items-end h-3">
                                                <div className="w-0.5 h-1 bg-green-400 animate-bounce" style={{ animationDelay: '0ms' }} />
                                                <div className="w-0.5 h-2 bg-green-400 animate-bounce" style={{ animationDelay: '100ms' }} />
                                                <div className="w-0.5 h-3 bg-green-400 animate-bounce" style={{ animationDelay: '200ms' }} />
                                            </div>
                                            <span className="text-[10px] font-bold uppercase tracking-wider">Audio Active</span>
                                        </div>
                                    ) : (
                                        <div className="flex items-center gap-2">
                                            <MicOff className="h-3 w-3 text-red-400" />
                                            <span className="text-[10px] font-bold uppercase tracking-wider text-red-400">Microphone Muted</span>
                                        </div>
                                    )}
                                </div>
                            </CardContent>
                        </Card>
                    </div>

                    {/* Controls */}
                    <div className="bg-background/80 backdrop-blur-md p-4 rounded-3xl border shadow-xl flex items-center justify-center gap-6 max-w-md mx-auto w-full">
                        <div className="flex flex-col items-center gap-1">
                            <Button
                                variant={isRecording ? "destructive" : "outline"}
                                size="icon"
                                className={`h-12 w-12 rounded-2xl transition-all ${isRecording ? 'animate-pulse' : ''}`}
                                onClick={toggleRecording}
                            >
                                {isRecording ? <Mic className="h-5 w-5" /> : <MicOff className="h-5 w-5" />}
                            </Button>
                            <span className="text-[10px] font-bold text-muted-foreground uppercase">{isRecording ? 'Recording...' : 'Voice Off'}</span>
                        </div>

                        <div className="flex flex-col items-center gap-1">
                            <Button
                                variant={isVideoOn ? "outline" : "destructive"}
                                size="icon"
                                className="h-12 w-12 rounded-2xl transition-all"
                                onClick={() => setIsVideoOn(!isVideoOn)}
                            >
                                {isVideoOn ? <Video className="h-5 w-5" /> : <VideoOff className="h-5 w-5" />}
                            </Button>
                            <span className="text-[10px] font-bold text-muted-foreground uppercase">{isVideoOn ? 'Stop Video' : 'Start Video'}</span>
                        </div>

                        <div className="h-10 w-px bg-border mx-2" />

                        <div className="flex flex-col items-center gap-1">
                            <Button
                                variant="destructive"
                                size="icon"
                                className="h-12 w-12 rounded-2xl shadow-lg shadow-red-500/20 hover:scale-110 active:scale-95 transition-all"
                                onClick={handleEndInterview}
                            >
                                <Phone className="h-5 w-5 rotate-[135deg]" />
                            </Button>
                            <span className="text-[10px] font-bold text-red-600 uppercase">End Session</span>
                        </div>
                    </div>
                </div>

                {/* Chat Section */}
                <div className="w-96 border-l flex flex-col bg-background/50 backdrop-blur-xl shrink-0">
                    <div className="p-4 border-b flex items-center justify-between">
                        <div className="flex items-center gap-2">
                            <div className="p-1.5 bg-primary/10 rounded-lg">
                                <MessageSquare className="h-4 w-4 text-primary" />
                            </div>
                            <h3 className="font-bold text-sm">Session Log</h3>
                        </div>
                        <div className="bg-muted px-2 py-0.5 rounded text-[10px] font-bold uppercase tracking-tighter">Live</div>
                    </div>

                    {/* Messages Container */}
                    <div className="flex-1 overflow-y-auto p-4 space-y-6">
                        {messages.length === 0 && (
                            <div className="h-full flex flex-col items-center justify-center text-center opacity-40 grayscale">
                                <Bot className="h-12 w-12 mb-2" />
                                <p className="text-xs font-medium">Interviewer is connecting...</p>
                            </div>
                        )}
                        {messages.map((msg) => (
                            <div
                                key={msg.id}
                                className={`flex flex-col ${msg.role === 'user' ? 'items-end' : 'items-start'}`}
                            >
                                <div className={`max-w-[85%] rounded-2xl px-4 py-3 relative ${msg.role === 'user'
                                    ? 'bg-primary text-primary-foreground rounded-tr-none shadow-lg shadow-primary/10'
                                    : 'bg-muted/50 border rounded-tl-none'
                                    }`}>
                                    <p className="text-sm leading-relaxed">{msg.content}</p>
                                    <div className={`mt-2 flex items-center justify-between gap-4 ${msg.role === 'user' ? 'text-primary-foreground/60' : 'text-muted-foreground'}`}>
                                        <p className="text-[9px] font-bold uppercase tracking-wider">
                                            {msg.role === 'user' ? 'You' : activeAvatar?.name}
                                        </p>
                                        <p className="text-[9px] font-mono">
                                            {msg.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                        </p>
                                    </div>
                                </div>
                            </div>
                        ))}
                        {(isThinking || isAIProcessing) && (
                            <div className="flex justify-start">
                                <div className="bg-muted/30 border rounded-2xl rounded-tl-none px-4 py-3">
                                    <div className="flex items-center gap-3">
                                        <div className="flex gap-1">
                                            <div className="w-1.5 h-1.5 bg-primary rounded-full animate-bounce [animation-delay:-0.3s]" />
                                            <div className="w-1.5 h-1.5 bg-primary rounded-full animate-bounce [animation-delay:-0.15s]" />
                                            <div className="w-1.5 h-1.5 bg-primary rounded-full animate-bounce" />
                                        </div>
                                        <span className="text-[10px] font-bold text-muted-foreground uppercase tracking-widest">
                                            {activeAvatar?.name} is speaking
                                        </span>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>

                    {/* Input Area */}
                    {!isCompleted ? (
                        <div className="p-4 border-t space-y-3 bg-background">
                            <div className="relative">
                                <textarea
                                    value={userResponse}
                                    onChange={(e) => setUserResponse(e.target.value)}
                                    placeholder="Click to type your response..."
                                    className="w-full min-h-[100px] p-4 text-sm rounded-2xl border bg-muted/20 resize-none focus:outline-none focus:ring-2 focus:ring-primary/20 transition-all placeholder:text-muted-foreground/50 font-medium"
                                    disabled={isAIProcessing || isThinking}
                                    onKeyDown={(e) => {
                                        if (e.key === 'Enter' && !e.shiftKey) {
                                            e.preventDefault()
                                            handleSendResponse()
                                        }
                                    }}
                                />
                                <div className="absolute bottom-3 right-3 text-[9px] font-bold text-muted-foreground uppercase opacity-50">
                                    Press Enter 
                                </div>
                            </div>
                            <Button
                                className="w-full h-11 rounded-xl font-bold uppercase tracking-widest shadow-lg shadow-primary/20 transition-all hover:scale-[1.02] active:scale-[0.98]"
                                onClick={() => handleSendResponse(false)}
                                disabled={!userResponse.trim() || isThinking || isAIProcessing}
                            >
                                <Send className="h-4 w-4 mr-2" />
                                Submit Answer
                            </Button>
                        </div>
                    ) : (
                        <div className="p-6 border-t text-center bg-green-500/5 space-y-4">
                            <div className="h-12 w-12 bg-green-500/20 rounded-full flex items-center justify-center mx-auto">
                                <CheckCircle2 className="h-6 w-6 text-green-600" />
                            </div>
                            <div className="space-y-1">
                                <p className="font-bold text-lg">Interview Concluded</p>
                                <p className="text-xs text-muted-foreground">Detailed feedback and scoring ready</p>
                            </div>
                            <Button
                                className="w-full rounded-full h-11 bg-green-600 hover:bg-green-700 font-bold"
                                onClick={handleEndInterview}
                            >
                                Generate Full Report
                                <ChevronRight className="h-4 w-4 ml-2" />
                            </Button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    )
}

"use client"

import * as React from 'react'
import { useState, useRef, useEffect } from 'react'
import { useRouter, useParams } from 'next/navigation'
import { useAuth } from '@/lib/auth-context'
import { interviewApi } from '@/lib/api'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import {
    ArrowLeft,
    ArrowRight,
    Check,
    Camera,
    Mic,
    Monitor,
    Wifi,
    Volume2,
    AlertCircle,
    Loader2,
    Play,
    CheckCircle2,
    XCircle,
    Lightbulb
} from 'lucide-react'

interface CheckItem {
    id: string
    label: string
    icon: React.ElementType
    status: 'pending' | 'checking' | 'success' | 'error'
    message?: string
}

export default function InterviewStartPage() {
    const router = useRouter()
    const params = useParams()
    const { isAuthenticated, isLoading: authLoading } = useAuth()

    const videoRef = useRef<HTMLVideoElement>(null)
    const [stream, setStream] = useState<MediaStream | null>(null)
    const [audioLevel, setAudioLevel] = useState(0)
    const [isReady, setIsReady] = useState(false)
    interface Interview {
        id: string
        role: string
        title?: string
    }
    const [interview, setInterview] = useState<Interview | null>(null)

    const [checks, setChecks] = useState<CheckItem[]>([
        { id: 'camera', label: 'Camera Access', icon: Camera, status: 'pending' },
        { id: 'microphone', label: 'Microphone Access', icon: Mic, status: 'pending' },
        { id: 'browser', label: 'Browser Compatibility', icon: Monitor, status: 'pending' },
        { id: 'connection', label: 'Internet Connection', icon: Wifi, status: 'pending' },
    ])

    useEffect(() => {
        if (!authLoading && !isAuthenticated) {
            router.push('/login')
        }
    }, [authLoading, isAuthenticated, router])

    // Fetch interview details
    useEffect(() => {
        const fetchInterview = async () => {
            try {
                const res = await interviewApi.getById(params.id as string)
                setInterview(res.data.interview)
            } catch (error) {
                console.error('Failed to fetch interview:', error)
            }
        }
        if (params.id) {
            fetchInterview()
        }
    }, [params.id])

    function updateCheck(id: string, update: Partial<CheckItem>) {
        setChecks(prev => prev.map(check =>
            check.id === id ? { ...check, ...update } : check
        ))
    }

    const requestMediaPermissions = async () => {
        updateCheck('camera', { status: 'checking' })
        updateCheck('microphone', { status: 'checking' })

        try {
            const mediaStream = await navigator.mediaDevices.getUserMedia({
                video: true,
                audio: true
            })

            setStream(mediaStream)

            // Attach video to preview
            if (videoRef.current) {
                videoRef.current.srcObject = mediaStream
            }

            updateCheck('camera', { status: 'success', message: 'Camera connected' })
            updateCheck('microphone', { status: 'success', message: 'Microphone connected' })

            // Setup audio level monitoring
            const audioContext = new AudioContext()
            const analyser = audioContext.createAnalyser()
            const microphone = audioContext.createMediaStreamSource(mediaStream)
            microphone.connect(analyser)
            analyser.fftSize = 256

            const dataArray = new Uint8Array(analyser.frequencyBinCount)

            const updateAudioLevel = () => {
                analyser.getByteFrequencyData(dataArray)
                const average = dataArray.reduce((a, b) => a + b) / dataArray.length
                setAudioLevel(Math.round((average / 255) * 100))
                requestAnimationFrame(updateAudioLevel)
            }
            updateAudioLevel()

            setIsReady(true)
        } catch (error) {
            console.error('Media permission error:', error)
            const err = error as Error

            if (err.name === 'NotAllowedError') {
                updateCheck('camera', { status: 'error', message: 'Permission denied - Please allow access' })
                updateCheck('microphone', { status: 'error', message: 'Permission denied - Please allow access' })
            } else {
                updateCheck('camera', { status: 'error', message: 'Camera not found' })
                updateCheck('microphone', { status: 'error', message: 'Microphone not found' })
            }
        }
    }

    const runSystemChecks = async () => {
        // Check browser compatibility
        updateCheck('browser', { status: 'checking' })
        await new Promise(r => setTimeout(r, 500))
        const isCompatible = !!(navigator.mediaDevices && navigator.mediaDevices.getUserMedia)
        updateCheck('browser', {
            status: isCompatible ? 'success' : 'error',
            message: isCompatible ? 'Chrome/Edge/Firefox detected' : 'Browser not supported'
        })

        // Check internet connection
        updateCheck('connection', { status: 'checking' })
        await new Promise(r => setTimeout(r, 500))
        const isOnline = navigator.onLine
        updateCheck('connection', {
            status: isOnline ? 'success' : 'error',
            message: isOnline ? 'Connection stable' : 'No internet connection'
        })

        // Request camera and microphone
        await requestMediaPermissions()
    }

    // Run system checks on mount
    useEffect(() => {
        let mounted = true
        if (mounted) {
            runSystemChecks()
        }
        return () => {
            mounted = false
            // Cleanup stream on unmount
            if (stream) {
                stream.getTracks().forEach(track => track.stop())
            }
        }
    }, [])

    const handleStartInterview = async () => {
        try {
            await interviewApi.start(params.id as string)
            router.push(`/interviews/${params.id}`)
        } catch (error) {
            console.error('Failed to start interview:', error)
        }
    }

    const getStatusIcon = (status: CheckItem['status']) => {
        switch (status) {
            case 'pending': return <div className="h-5 w-5 rounded-full border-2 border-muted" />
            case 'checking': return <Loader2 className="h-5 w-5 animate-spin text-primary" />
            case 'success': return <CheckCircle2 className="h-5 w-5 text-green-500" />
            case 'error': return <XCircle className="h-5 w-5 text-red-500" />
        }
    }

    const allChecksPassed = checks.every(c => c.status === 'success')

    if (authLoading || !isAuthenticated) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <Loader2 className="h-8 w-8 animate-spin text-primary" />
            </div>
        )
    }

    return (
        <div className="min-h-screen bg-gradient-to-br from-background to-muted/30 py-8">
            <div className="container max-w-4xl">
                {/* Header */}
                <div className="mb-8">
                    <Button variant="ghost" onClick={() => router.push('/interviews')} className="mb-4">
                        <ArrowLeft className="h-4 w-4 mr-2" />
                        Back to Interviews
                    </Button>
                    <h1 className="text-3xl font-bold">Pre-Interview Setup</h1>
                    <p className="text-muted-foreground">Let&apos;s make sure everything is ready for your interview</p>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    {/* Camera Preview */}
                    <Card>
                        <CardHeader>
                            <CardTitle className="flex items-center gap-2">
                                <Camera className="h-5 w-5" />
                                Camera Preview
                            </CardTitle>
                        </CardHeader>
                        <CardContent>
                            <div className="aspect-video bg-muted rounded-lg overflow-hidden relative">
                                <video
                                    ref={videoRef}
                                    autoPlay
                                    muted
                                    playsInline
                                    className="w-full h-full object-cover scale-x-[-1]"
                                />
                                {!stream && (
                                    <div className="absolute inset-0 flex items-center justify-center">
                                        <div className="text-center text-muted-foreground">
                                            <Camera className="h-12 w-12 mx-auto mb-2 opacity-50" />
                                            <p>Camera loading...</p>
                                        </div>
                                    </div>
                                )}
                            </div>
                        </CardContent>
                    </Card>

                    {/* System Checks */}
                    <Card>
                        <CardHeader>
                            <CardTitle className="flex items-center gap-2">
                                <Monitor className="h-5 w-5" />
                                System Check
                            </CardTitle>
                            <CardDescription>
                                We&apos;re checking your setup for the best interview experience
                            </CardDescription>
                        </CardHeader>
                        <CardContent className="space-y-4">
                            {checks.map(check => (
                                <div key={check.id} className="flex items-center justify-between p-3 bg-muted/50 rounded-lg">
                                    <div className="flex items-center gap-3">
                                        <check.icon className="h-5 w-5 text-muted-foreground" />
                                        <div>
                                            <p className="font-medium text-sm">{check.label}</p>
                                            {check.message && (
                                                <p className={`text-xs ${check.status === 'error' ? 'text-red-500' : 'text-muted-foreground'}`}>
                                                    {check.message}
                                                </p>
                                            )}
                                        </div>
                                    </div>
                                    {getStatusIcon(check.status)}
                                </div>
                            ))}

                            {/* Audio Level Indicator */}
                            {stream && (
                                <div className="p-3 bg-muted/50 rounded-lg">
                                    <div className="flex items-center gap-3 mb-2">
                                        <Volume2 className="h-5 w-5 text-muted-foreground" />
                                        <span className="font-medium text-sm">Microphone Level</span>
                                    </div>
                                    <div className="h-2 bg-muted rounded-full overflow-hidden">
                                        <div
                                            className="h-full bg-gradient-to-r from-green-500 to-green-400 transition-all duration-100"
                                            style={{ width: `${audioLevel}%` }}
                                        />
                                    </div>
                                    <p className="text-xs text-muted-foreground mt-1">
                                        Speak to test your microphone
                                    </p>
                                </div>
                            )}
                        </CardContent>
                    </Card>
                </div>

                {/* Interview Tips */}
                <Card className="mt-6">
                    <CardHeader>
                        <CardTitle className="flex items-center gap-2">
                            <Lightbulb className="h-5 w-5 text-yellow-500" />
                            Tips for a Great Interview
                        </CardTitle>
                    </CardHeader>
                    <CardContent>
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                            <div className="p-4 bg-muted/50 rounded-lg">
                                <h4 className="font-medium mb-1"> Be Specific</h4>
                                <p className="text-sm text-muted-foreground">Use the STAR method for behavioral questions</p>
                            </div>
                            <div className="p-4 bg-muted/50 rounded-lg">
                                <h4 className="font-medium mb-1"> Think Aloud</h4>
                                <p className="text-sm text-muted-foreground">Explain your thought process for technical questions</p>
                            </div>
                            <div className="p-4 bg-muted/50 rounded-lg">
                                <h4 className="font-medium mb-1"> Pace Yourself</h4>
                                <p className="text-sm text-muted-foreground">Take your time - quality over speed</p>
                            </div>
                        </div>
                    </CardContent>
                </Card>

                {/* Start Button */}
                <div className="mt-8 text-center">
                    {allChecksPassed ? (
                        <Button size="lg" onClick={handleStartInterview} className="px-8">
                            <Play className="h-5 w-5 mr-2" />
                            I&apos;m Ready - Start Interview
                        </Button>
                    ) : (
                        <div className="space-y-2">
                            <Button size="lg" disabled className="px-8">
                                <AlertCircle className="h-5 w-5 mr-2" />
                                Complete System Check First
                            </Button>
                            <p className="text-sm text-muted-foreground">
                                Please allow camera and microphone access to continue
                            </p>
                            <Button variant="outline" onClick={requestMediaPermissions}>
                                Retry Permissions
                            </Button>
                        </div>
                    )}
                </div>
            </div>
        </div>
    )
}

"use client"

import * as React from 'react'
import { useState } from 'react'
import { useRouter } from 'next/navigation'
import { useAuth } from '@/lib/auth-context'
import { interviewApi } from '@/lib/api'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Textarea } from '@/components/ui/textarea'
import {
    Select,
    SelectContent,
    SelectItem,
    SelectTrigger,
    SelectValue,
} from "@/components/ui/select"
import {
    ArrowLeft,
    ArrowRight,
    Check,
    Upload,
    FileText,
    User,
    Calendar,
    Clock,
    Loader2,
    Briefcase,
    Bot,
    Target,
    Sparkles,
    Video
} from 'lucide-react'

// Step configuration
const STEPS = [
    { id: 1, title: 'Interview Details', icon: Target },
    { id: 2, title: 'JD & Resume', icon: FileText },
    { id: 3, title: 'Select Panel', icon: User },
    { id: 4, title: 'Schedule', icon: Calendar },
    { id: 5, title: 'Tech Check', icon: Bot },
    { id: 6, title: 'Review', icon: Check },
]

const DOMAINS = [
    { value: 'TECHNOLOGY', label: 'Technology / Software' },
    { value: 'DATA_SCIENCE', label: 'Data Science & AI' },
    { value: 'PRODUCT', label: 'Product Management' },
    { value: 'DESIGN', label: 'UI/UX Design' },
    { value: 'BUSINESS', label: 'Business / Consulting' },
    { value: 'FINANCE', label: 'Finance & Banking' },
]

const DIFFICULTIES = [
    { value: 'BEGINNER', label: 'Beginner', desc: 'For freshers, 0-2 years experience' },
    { value: 'INTERMEDIATE', label: 'Intermediate', desc: 'For mid-level, 2-5 years experience' },
    { value: 'ADVANCED', label: 'Advanced', desc: 'For seniors, 5+ years experience' },
]

const AVATARS = [
    { id: 'tech-1', name: 'Alex Chen', role: 'Technical Lead', personality: 'Friendly', avatar: '/interviewer_avatar.png' },
    { id: 'tech-2', name: 'Sarah Johnson', role: 'Senior Engineer', personality: 'Professional', avatar: '/interviewer_avatar.png' },
    { id: 'hr-1', name: 'Emma Williams', role: 'HR Manager', personality: 'Encouraging', avatar: '/interviewer_avatar.png' },
    { id: 'hr-2', name: 'David Smith', role: 'Talent Acquisition', personality: 'Strict', avatar: '/interviewer_avatar.png' },
]

const TIME_SLOTS = [
    '09:00 AM', '10:00 AM', '11:00 AM', '12:00 PM',
    '02:00 PM', '03:00 PM', '04:00 PM', '05:00 PM'
]

export default function NewInterviewPage() {
    const router = useRouter()
    const { isAuthenticated, isLoading: authLoading } = useAuth()

    const [currentStep, setCurrentStep] = useState(1)
    const [isSubmitting, setIsSubmitting] = useState(false)

    // Form state
    const [formData, setFormData] = useState({
        // Step 1
        domain: 'TECHNOLOGY',
        role: '',
        company: '',
        technology: '',
        difficulty: 'INTERMEDIATE',
        duration: 30,
        type: 'INSTANT', // 'INSTANT' or 'SCHEDULED'
        // Step 2
        jobDescription: '',
        resumeFile: null as File | null,
        resumeUrl: '',
        // Step 3
        selectedAvatars: ['tech-1', 'hr-1'],
        panelCount: 2,
        // Step 4
        scheduledDate: '',
        scheduledTime: '',
        // Step 5
        techVerified: false
    })

    React.useEffect(() => {
        if (!authLoading && !isAuthenticated) {
            router.push('/login')
        }
    }, [authLoading, isAuthenticated, router])

    const updateFormData = (field: string, value: any) => {
        setFormData(prev => ({ ...prev, [field]: value }))
    }

    const handleResumeUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0]
        if (file) {
            updateFormData('resumeFile', file)
            updateFormData('resumeUrl', file.name)
        }
    }

    const toggleAvatar = (avatarId: string) => {
        setFormData(prev => {
            const isSelected = prev.selectedAvatars.includes(avatarId)
            const newAvatars = isSelected
                ? prev.selectedAvatars.filter(id => id !== avatarId)
                : [...prev.selectedAvatars, avatarId]
            return {
                ...prev,
                selectedAvatars: newAvatars,
                panelCount: newAvatars.length
            }
        })
    }

    const canProceed = () => {
        switch (currentStep) {
            case 1: return formData.domain && formData.role && formData.technology
            case 2: return true // JD and resume are optional
            case 3: return formData.selectedAvatars.length > 0
            case 4: return formData.scheduledDate && formData.scheduledTime
            case 5: return formData.techVerified
            case 6: return true
            default: return true
        }
    }

    const handleNext = () => {
        if (currentStep === 3 && formData.type === 'INSTANT') {
            setCurrentStep(5)
            return
        }
        if (currentStep < 6 && canProceed()) {
            setCurrentStep(prev => prev + 1)
        }
    }

    const handleBack = () => {
        if (currentStep === 5 && formData.type === 'INSTANT') {
            setCurrentStep(3)
            return
        }
        if (currentStep > 1) {
            setCurrentStep(prev => prev - 1)
        }
    }

    const handleSubmit = async () => {
        setIsSubmitting(true)
        try {
            // Combine date and time for scheduledAt
            const scheduledAt = formData.scheduledDate && formData.scheduledTime
                ? new Date(`${formData.scheduledDate} ${formData.scheduledTime}`).toISOString()
                : null

            const response = await interviewApi.create({
                domain: formData.domain,
                role: formData.role,
                company: formData.company,
                difficulty: formData.difficulty,
                jobDescription: formData.jobDescription,
                panelCount: formData.panelCount,
                scheduledAt,
                duration: formData.duration,
                selectedAvatars: formData.selectedAvatars,
                technology: formData.technology // Added
            })

            const interviewId = response.data.interview.id

            // Even for INSTANT, we now redirect to the start/technical-check page as requested by user
            // This ensures prerequisites are checked before entering the room
            router.push(`/interviews/${interviewId}/start`)
        } catch (error) {
            console.error('Failed to create interview:', error)
            alert('Failed to schedule interview. Please try again.')
        } finally {
            setIsSubmitting(false)
        }
    }

    // Dynamic duration estimation helper
    React.useEffect(() => {
        let estimatedDuration = 30 // Base

        if (formData.jobDescription) estimatedDuration += 10
        if (formData.resumeFile) estimatedDuration += 5
        if (formData.difficulty === 'ADVANCED') estimatedDuration += 15
        if (formData.difficulty === 'BEGINNER') estimatedDuration -= 5
        if (formData.panelCount > 1) estimatedDuration += (formData.panelCount - 1) * 5

        if (estimatedDuration !== formData.duration) {
            updateFormData('duration', estimatedDuration)
        }
    }, [formData.jobDescription, formData.resumeFile, formData.difficulty, formData.panelCount])


    if (authLoading || !isAuthenticated) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <Loader2 className="h-8 w-8 animate-spin text-primary" />
            </div>
        )
    }

    return (
        <div className="min-h-screen bg-gradient-to-br from-background to-muted/30 py-8">
            <div className="container max-w-4xl">
                {/* Header */}
                <div className="mb-8">
                    <Button variant="ghost" onClick={() => router.push('/interviews')} className="mb-4">
                        <ArrowLeft className="h-4 w-4 mr-2" />
                        Back to Interviews
                    </Button>
                    <h1 className="text-3xl font-bold">Schedule AI Interview</h1>
                    <p className="text-muted-foreground">Complete the steps below to set up your personalized mock interview</p>
                </div>

                {/* Progress Steps */}
                <div className="mb-8">
                    <div className="flex items-center justify-between">
                        {STEPS.map((step, idx) => (
                            <div key={step.id} className="flex items-center">
                                <div className={`flex items-center justify-center w-10 h-10 rounded-full border-2 transition-colors ${currentStep > step.id
                                    ? 'bg-primary border-primary text-primary-foreground'
                                    : currentStep === step.id
                                        ? 'border-primary text-primary'
                                        : 'border-muted text-muted-foreground'
                                    }`}>
                                    {currentStep > step.id ? (
                                        <Check className="h-5 w-5" />
                                    ) : (
                                        <step.icon className="h-5 w-5" />
                                    )}
                                </div>
                                {idx < STEPS.length - 1 && (
                                    <div className={`w-16 md:w-24 h-0.5 mx-2 ${currentStep > step.id ? 'bg-primary' : 'bg-muted'
                                        }`} />
                                )}
                            </div>
                        ))}
                    </div>
                    <div className="flex justify-between mt-2">
                        {STEPS.map(step => (
                            <span key={step.id} className={`text-xs ${currentStep === step.id ? 'text-primary font-medium' : 'text-muted-foreground'
                                }`}>
                                {step.title}
                            </span>
                        ))}
                    </div>
                </div>

                {/* Step Content */}
                <Card className="mb-6">
                    <CardHeader>
                        <CardTitle>{STEPS[currentStep - 1].title}</CardTitle>
                        <CardDescription>
                            {currentStep === 1 && 'Tell us about the role you want to practice for'}
                            {currentStep === 2 && 'Upload your resume and job description for personalized questions'}
                            {currentStep === 3 && 'Choose your interviewer panel'}
                            {currentStep === 4 && 'Pick a convenient time for your interview'}
                            {currentStep === 5 && 'Review your interview setup'}
                        </CardDescription>
                    </CardHeader>
                    <CardContent>
                        {/* Step 1: Interview Details */}
                        {currentStep === 1 && (
                            <div className="space-y-6">
                                <div className="space-y-4">
                                    <Label className="text-base">Interview Type</Label>
                                    <div className="grid grid-cols-2 gap-4">
                                        <Card
                                            className={`cursor-pointer transition-all ${formData.type === 'INSTANT' ? 'border-primary ring-2 ring-primary/20' : 'hover:border-primary/50'}`}
                                            onClick={() => updateFormData('type', 'INSTANT')}
                                        >
                                            <CardContent className="p-4 flex flex-col items-center gap-2 text-center">
                                                <Sparkles className={`h-8 w-8 ${formData.type === 'INSTANT' ? 'text-primary' : 'text-muted-foreground'}`} />
                                                <div className="font-bold">Instant Start</div>
                                                <div className="text-xs text-muted-foreground">Start practicing immediately</div>
                                            </CardContent>
                                        </Card>
                                        <Card
                                            className={`cursor-pointer transition-all ${formData.type === 'SCHEDULED' ? 'border-primary ring-2 ring-primary/20' : 'hover:border-primary/50'}`}
                                            onClick={() => updateFormData('type', 'SCHEDULED')}
                                        >
                                            <CardContent className="p-4 flex flex-col items-center gap-2 text-center">
                                                <Calendar className={`h-8 w-8 ${formData.type === 'SCHEDULED' ? 'text-primary' : 'text-muted-foreground'}`} />
                                                <div className="font-bold">Schedule Later</div>
                                                <div className="text-xs text-muted-foreground">Pick a time for your session</div>
                                            </CardContent>
                                        </Card>
                                    </div>
                                </div>

                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <div className="space-y-2">
                                        <Label>Domain / Industry</Label>
                                        <Select value={formData.domain} onValueChange={(v) => updateFormData('domain', v)}>
                                            <SelectTrigger>
                                                <SelectValue />
                                            </SelectTrigger>
                                            <SelectContent>
                                                {DOMAINS.map(d => (
                                                    <SelectItem key={d.value} value={d.value}>{d.label}</SelectItem>
                                                ))}
                                            </SelectContent>
                                        </Select>
                                    </div>
                                    <div className="space-y-2">
                                        <Label>Role / Position *</Label>
                                        <Input
                                            placeholder="e.g., Frontend Developer"
                                            value={formData.role}
                                            onChange={(e) => updateFormData('role', e.target.value)}
                                            required
                                        />
                                    </div>
                                    <div className="space-y-2">
                                        <Label>Primary Technology Stack *</Label>
                                        <Input
                                            placeholder="e.g., React, Node.js, Python"
                                            value={formData.technology}
                                            onChange={(e) => updateFormData('technology', e.target.value)}
                                            required
                                        />
                                    </div>
                                    <div className="space-y-2">
                                        <Label>Target Company (Optional)</Label>
                                        <Input
                                            placeholder="e.g., Google, Amazon"
                                            value={formData.company}
                                            onChange={(e) => updateFormData('company', e.target.value)}
                                        />
                                    </div>
                                    <div className="space-y-2">
                                        <Label>Difficulty Level</Label>
                                        <Select value={formData.difficulty} onValueChange={(v) => updateFormData('difficulty', v)}>
                                            <SelectTrigger>
                                                <SelectValue />
                                            </SelectTrigger>
                                            <SelectContent>
                                                {DIFFICULTIES.map(d => (
                                                    <SelectItem key={d.value} value={d.value}>
                                                        <div>
                                                            <div className="font-medium">{d.label}</div>
                                                            <div className="text-xs text-muted-foreground">{d.desc}</div>
                                                        </div>
                                                    </SelectItem>
                                                ))}
                                            </SelectContent>
                                        </Select>
                                    </div>
                                    <div className="space-y-2">
                                        <Label>Duration (Minutes)</Label>
                                        <Select value={String(formData.duration)} onValueChange={(v) => updateFormData('duration', Number(v))}>
                                            <SelectTrigger>
                                                <SelectValue />
                                            </SelectTrigger>
                                            <SelectContent>
                                                {[15, 30, 45, 60].map(m => (
                                                    <SelectItem key={m} value={String(m)}>{m} Minutes</SelectItem>
                                                ))}
                                            </SelectContent>
                                        </Select>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Step 2: JD & Resume */}
                        {currentStep === 2 && (
                            <div className="space-y-6">
                                <div className="space-y-2">
                                    <Label>Job Description</Label>
                                    <p className="text-sm text-muted-foreground mb-2">
                                        Paste the job description for more relevant questions
                                    </p>
                                    <Textarea
                                        placeholder="Paste the job description here..."
                                        value={formData.jobDescription}
                                        onChange={(e) => updateFormData('jobDescription', e.target.value)}
                                        rows={6}
                                    />
                                </div>
                                <div className="space-y-2">
                                    <Label>Resume Upload</Label>
                                    <p className="text-sm text-muted-foreground mb-2">
                                        Upload your resume to get experience-based questions
                                    </p>
                                    <div className="border-2 border-dashed rounded-lg p-8 text-center hover:border-primary transition-colors">
                                        <input
                                            type="file"
                                            accept=".pdf,.doc,.docx"
                                            onChange={handleResumeUpload}
                                            className="hidden"
                                            id="resume-upload"
                                        />
                                        <label htmlFor="resume-upload" className="cursor-pointer">
                                            {formData.resumeFile ? (
                                                <div className="flex items-center justify-center gap-2">
                                                    <FileText className="h-8 w-8 text-primary" />
                                                    <div className="text-left">
                                                        <p className="font-medium">{formData.resumeFile.name}</p>
                                                        <p className="text-sm text-muted-foreground">Click to change</p>
                                                    </div>
                                                </div>
                                            ) : (
                                                <>
                                                    <Upload className="h-8 w-8 mx-auto text-muted-foreground mb-2" />
                                                    <p className="font-medium">Drop your resume here or click to upload</p>
                                                    <p className="text-sm text-muted-foreground">PDF, DOC, DOCX (Max 5MB)</p>
                                                </>
                                            )}
                                        </label>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Step 3: Panel Selection */}
                        {currentStep === 3 && (
                            <div className="space-y-6">
                                <p className="text-sm text-muted-foreground">
                                    Select the interviewers for your panel. Mix technical and HR for a realistic experience.
                                </p>
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                    {AVATARS.map(avatar => {
                                        const isSelected = formData.selectedAvatars.includes(avatar.id)
                                        return (
                                            <Card
                                                key={avatar.id}
                                                className={`cursor-pointer transition-all ${isSelected ? 'border-primary ring-2 ring-primary/20' : 'hover:border-primary/50'
                                                    }`}
                                                onClick={() => toggleAvatar(avatar.id)}
                                            >
                                                <CardContent className="flex items-center gap-4 p-4">
                                                    <div className={`w-14 h-14 rounded-full flex items-center justify-center ${avatar.role.includes('Technical') || avatar.role.includes('Engineer')
                                                        ? 'bg-blue-100 text-blue-600'
                                                        : 'bg-purple-100 text-purple-600'
                                                        }`}>
                                                        {avatar.role.includes('Technical') || avatar.role.includes('Engineer') ? (
                                                            <Bot className="h-7 w-7" />
                                                        ) : (
                                                            <Briefcase className="h-7 w-7" />
                                                        )}
                                                    </div>
                                                    <div className="flex-1">
                                                        <h4 className="font-medium">{avatar.name}</h4>
                                                        <p className="text-sm text-muted-foreground">{avatar.role}</p>
                                                        <p className="text-xs text-muted-foreground">Style: {avatar.personality}</p>
                                                    </div>
                                                    <div className={`w-6 h-6 rounded-full border-2 flex items-center justify-center ${isSelected ? 'border-primary bg-primary text-primary-foreground' : 'border-muted'
                                                        }`}>
                                                        {isSelected && <Check className="h-4 w-4" />}
                                                    </div>
                                                </CardContent>
                                            </Card>
                                        )
                                    })}
                                </div>
                                <p className="text-sm font-medium">
                                    Selected: {formData.selectedAvatars.length} interviewer(s)
                                </p>
                            </div>
                        )}

                        {/* Step 4: Schedule */}
                        {currentStep === 4 && (
                            <div className="space-y-6">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <div className="space-y-2">
                                        <Label>Select Date</Label>
                                        <Input
                                            type="date"
                                            value={formData.scheduledDate}
                                            onChange={(e) => updateFormData('scheduledDate', e.target.value)}
                                            min={new Date().toISOString().split('T')[0]}
                                        />
                                    </div>
                                    <div className="space-y-2">
                                        <Label>Select Time Slot</Label>
                                        <div className="grid grid-cols-2 gap-2">
                                            {TIME_SLOTS.map(slot => (
                                                <Button
                                                    key={slot}
                                                    variant={formData.scheduledTime === slot ? 'default' : 'outline'}
                                                    size="sm"
                                                    onClick={() => updateFormData('scheduledTime', slot)}
                                                    className="justify-start"
                                                >
                                                    <Clock className="h-4 w-4 mr-2" />
                                                    {slot}
                                                </Button>
                                            ))}
                                        </div>
                                    </div>
                                </div>
                                <div className="p-4 bg-muted/50 rounded-lg">
                                    <div className="flex items-center gap-2 text-sm">
                                        <Sparkles className="h-4 w-4 text-primary" />
                                        <span className="font-medium">Pro Tip:</span>
                                        <span className="text-muted-foreground">Schedule when you&apos;re most alert for best performance</span>
                                    </div>
                                </div>
                            </div>
                        )}

                        {/* Step 5: Tech Check */}
                        {currentStep === 5 && (
                            <div className="space-y-6">
                                <p className="text-sm text-muted-foreground">
                                    Before we begin, let&apos;s ensure your technical setup is ready for a professional interview.
                                </p>
                                <div className="space-y-4">
                                    <div className="flex items-center justify-between p-4 border rounded-lg bg-background/50">
                                        <div className="flex items-center gap-3">
                                            <div className="p-2 bg-blue-100 text-blue-600 rounded-full">
                                                <Video className="h-5 w-5" />
                                            </div>
                                            <div>
                                                <p className="font-medium">Camera Check</p>
                                                <p className="text-xs text-muted-foreground">Ensure you are clearly visible</p>
                                            </div>
                                        </div>
                                        <Check className="h-5 w-5 text-green-500" />
                                    </div>
                                    <div className="flex items-center justify-between p-4 border rounded-lg bg-background/50">
                                        <div className="flex items-center gap-3">
                                            <div className="p-2 bg-purple-100 text-purple-600 rounded-full">
                                                <Clock className="h-5 w-5" />
                                            </div>
                                            <div>
                                                <p className="font-medium">Microphone Check</p>
                                                <p className="text-xs text-muted-foreground">Audio should be clear and loud</p>
                                            </div>
                                        </div>
                                        <Check className="h-5 w-5 text-green-500" />
                                    </div>
                                    <div className="flex items-center justify-between p-4 border rounded-lg bg-background/50">
                                        <div className="flex items-center gap-3">
                                            <div className="p-2 bg-orange-100 text-orange-600 rounded-full">
                                                <Sparkles className="h-5 w-5" />
                                            </div>
                                            <div>
                                                <p className="font-medium">Internet Stability</p>
                                                <p className="text-xs text-muted-foreground">High-speed connection required</p>
                                            </div>
                                        </div>
                                        <Check className="h-5 w-5 text-green-500" />
                                    </div>
                                </div>
                                <div className="flex items-center space-x-2 pt-4">
                                    <input
                                        type="checkbox"
                                        id="tech-confirm"
                                        aria-label="Confirm technical setup"
                                        className="h-4 w-4 rounded border-gray-300 text-primary focus:ring-primary"
                                        checked={formData.techVerified}
                                        onChange={(e) => updateFormData('techVerified', e.target.checked)}
                                    />
                                    <Label htmlFor="tech-confirm" className="text-sm cursor-pointer">
                                        I confirm that my camera, microphone, and internet are working properly.
                                    </Label>
                                </div>
                            </div>
                        )}

                        {/* Step 6: Review */}
                        {currentStep === 6 && (
                            <div className="space-y-6">
                                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                                    <div className="space-y-4">
                                        <h4 className="font-medium flex items-center gap-2">
                                            <Target className="h-4 w-4 text-primary" />
                                            Role & Technology
                                        </h4>
                                        <div className="space-y-2 text-sm">
                                            <div className="flex justify-between">
                                                <span className="text-muted-foreground">Domain:</span>
                                                <span className="font-medium">{DOMAINS.find(d => d.value === formData.domain)?.label}</span>
                                            </div>
                                            <div className="flex justify-between">
                                                <span className="text-muted-foreground">Role:</span>
                                                <span className="font-medium">{formData.role}</span>
                                            </div>
                                            <div className="flex justify-between">
                                                <span className="text-muted-foreground">Technology:</span>
                                                <span className="font-medium">{formData.technology}</span>
                                            </div>
                                            {formData.company && (
                                                <div className="flex justify-between">
                                                    <span className="text-muted-foreground">Company:</span>
                                                    <span className="font-medium">{formData.company}</span>
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                    <div className="space-y-4">
                                        <h4 className="font-medium flex items-center gap-2">
                                            <Calendar className="h-4 w-4 text-primary" />
                                            Schedule & Panel
                                        </h4>
                                        <div className="space-y-2 text-sm">
                                            <div className="flex justify-between">
                                                <span className="text-muted-foreground">Type:</span>
                                                <span className="font-medium uppercase">{formData.type}</span>
                                            </div>
                                            {formData.type === 'SCHEDULED' && (
                                                <>
                                                    <div className="flex justify-between">
                                                        <span className="text-muted-foreground">Date:</span>
                                                        <span className="font-medium">
                                                            {formData.scheduledDate ? new Date(formData.scheduledDate).toLocaleDateString() : 'Not set'}
                                                        </span>
                                                    </div>
                                                    <div className="flex justify-between">
                                                        <span className="text-muted-foreground">Time:</span>
                                                        <span className="font-medium">{formData.scheduledTime || 'Not set'}</span>
                                                    </div>
                                                </>
                                            )}
                                            <div className="flex justify-between">
                                                <span className="text-muted-foreground">Panel Size:</span>
                                                <span className="font-medium">{formData.panelCount} interviewer(s)</span>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                                {formData.jobDescription && (
                                    <div className="p-4 bg-muted/50 rounded-lg border">
                                        <h4 className="font-medium mb-1 text-sm">Job Description</h4>
                                        <p className="text-xs text-muted-foreground line-clamp-3">{formData.jobDescription}</p>
                                    </div>
                                )}
                                {formData.resumeFile && (
                                    <div className="p-4 bg-muted/50 rounded-lg flex items-center gap-2 border">
                                        <FileText className="h-4 w-4 text-primary" />
                                        <span className="text-sm font-medium">Resume Uploaded:</span>
                                        <span className="text-xs text-muted-foreground">{formData.resumeFile.name}</span>
                                    </div>
                                )}
                                <div className="p-4 bg-green-50 border border-green-100 rounded-lg flex items-center gap-2">
                                    <Check className="h-4 w-4 text-green-600" />
                                    <span className="text-sm text-green-700 font-medium">Technical Setup Verified</span>
                                </div>
                            </div>
                        )}
                    </CardContent>
                </Card>

                {/* Navigation Buttons */}
                <div className="flex justify-between">
                    <Button variant="outline" onClick={handleBack} disabled={currentStep === 1}>
                        <ArrowLeft className="h-4 w-4 mr-2" />
                        Back
                    </Button>
                    {currentStep < 6 ? (
                        <Button onClick={handleNext} disabled={!canProceed()}>
                            Next
                            <ArrowRight className="h-4 w-4 ml-2" />
                        </Button>
                    ) : (
                        <Button onClick={handleSubmit} disabled={isSubmitting} className="bg-primary hover:bg-primary/90">
                            {isSubmitting ? (
                                <>
                                    <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                                    Initializing...
                                </>
                            ) : (
                                <>
                                    <Sparkles className="h-4 w-4 mr-2" />
                                    {formData.type === 'INSTANT' ? 'Start Interview Now' : 'Confirm & Schedule'}
                                </>
                            )}
                        </Button>
                    )}
                </div>
            </div>
        </div>
    )
}

"use client"

import * as React from 'react'
import { useRouter } from 'next/navigation'
import { interviewApi } from '@/lib/api'
import { useAuth } from '@/lib/auth-context'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import {
    Video,
    Calendar,
    Clock,
    CheckCircle2,
    XCircle,
    PlayCircle,
    Loader2,
    Plus,
    TrendingUp
} from 'lucide-react'

interface Interview {
    id: string
    domain: string
    role: string
    company?: string
    difficulty: string
    status: string
    scheduledAt: string
    score?: number
}

const domains = [
    { value: 'TECHNOLOGY', label: 'Technology' },
    { value: 'DATA_SCIENCE', label: 'Data Science' },
    { value: 'PRODUCT', label: 'Product Management' },
    { value: 'DESIGN', label: 'UI/UX Design' },
    { value: 'BUSINESS', label: 'Business/Consulting' },
]

const difficulties = [
    { value: 'EASY', label: 'Easy' },
    { value: 'MEDIUM', label: 'Medium' },
    { value: 'HARD', label: 'Hard' },
]

export default function InterviewsPage() {
    const router = useRouter()
    const { isAuthenticated, isLoading: authLoading } = useAuth()

    const [interviews, setInterviews] = React.useState<Interview[]>([])
    const [stats, setStats] = React.useState<{ total: number; completed: number; averageScore: number } | null>(null)
    const [isLoading, setIsLoading] = React.useState(true)

    React.useEffect(() => {
        if (!authLoading && !isAuthenticated) {
            router.push('/login')
        }
    }, [authLoading, isAuthenticated, router])

    React.useEffect(() => {
        const fetchData = async () => {
            if (!isAuthenticated) return

            try {
                const [interviewsRes, statsRes] = await Promise.all([
                    interviewApi.getAll(),
                    interviewApi.getStats(),
                ])
                setInterviews(interviewsRes.data.interviews || [])
                setStats(statsRes.data.stats)
            } catch (error) {
                console.error('Failed to fetch interviews:', error)
            } finally {
                setIsLoading(false)
            }
        }

        if (isAuthenticated) {
            fetchData()
        }
    }, [isAuthenticated])

    const handleStartInterview = async (id: string) => {
        try {
            await interviewApi.start(id)
            setInterviews(interviews.map(i =>
                i.id === id ? { ...i, status: 'IN_PROGRESS' } : i
            ))
            router.push(`/interviews/${id}`)
        } catch (error) {
            console.error('Failed to start interview:', error)
        }
    }

    const getStatusIcon = (status: string) => {
        switch (status) {
            case 'SCHEDULED': return <Calendar className="h-4 w-4 text-blue-500" />
            case 'IN_PROGRESS': return <PlayCircle className="h-4 w-4 text-yellow-500" />
            case 'COMPLETED': return <CheckCircle2 className="h-4 w-4 text-green-500" />
            case 'CANCELLED': return <XCircle className="h-4 w-4 text-red-500" />
            default: return <Clock className="h-4 w-4" />
        }
    }

    const getStatusColor = (status: string) => {
        switch (status) {
            case 'SCHEDULED': return 'bg-blue-100 text-blue-700 dark:bg-blue-900/30 dark:text-blue-400'
            case 'IN_PROGRESS': return 'bg-yellow-100 text-yellow-700 dark:bg-yellow-900/30 dark:text-yellow-400'
            case 'COMPLETED': return 'bg-green-100 text-green-700 dark:bg-green-900/30 dark:text-green-400'
            case 'CANCELLED': return 'bg-red-100 text-red-700 dark:bg-red-900/30 dark:text-red-400'
            default: return 'bg-gray-100 text-gray-700'
        }
    }

    if (authLoading || !isAuthenticated) {
        return (
            <div className="min-h-[calc(100vh-4rem)] flex items-center justify-center">
                <Loader2 className="h-8 w-8 animate-spin text-primary" />
            </div>
        )
    }

    return (
        <div className="container py-8">
            {/* Header */}
            <div className="flex items-center justify-between mb-8">
                <div>
                    <h1 className="text-3xl font-bold mb-2">AI Interview Prep</h1>
                    <p className="text-muted-foreground">
                        Practice with AI-powered mock interviews and get instant feedback
                    </p>
                </div>
                <Button onClick={() => router.push('/interviews/new')} className="rounded-xl shadow-lg shadow-primary/20 hover:scale-105 transition-all">
                    <Plus className="mr-2 h-4 w-4" /> New Interview
                </Button>
            </div>

            {/* Stats */}
            {stats && (
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
                    <Card>
                        <CardHeader className="pb-2">
                            <CardTitle className="text-sm font-medium text-muted-foreground">
                                Total Interviews
                            </CardTitle>
                        </CardHeader>
                        <CardContent>
                            <div className="text-2xl font-bold">{stats.total}</div>
                        </CardContent>
                    </Card>
                    <Card>
                        <CardHeader className="pb-2">
                            <CardTitle className="text-sm font-medium text-muted-foreground">
                                Completed
                            </CardTitle>
                        </CardHeader>
                        <CardContent>
                            <div className="text-2xl font-bold text-green-600">{stats.completed}</div>
                        </CardContent>
                    </Card>
                    <Card>
                        <CardHeader className="pb-2">
                            <CardTitle className="text-sm font-medium text-muted-foreground">
                                Average Score
                            </CardTitle>
                        </CardHeader>
                        <CardContent>
                            <div className="text-2xl font-bold flex items-center gap-2">
                                {Math.round(stats.averageScore || 0)}%
                                <TrendingUp className="h-5 w-5 text-green-500" />
                            </div>
                        </CardContent>
                    </Card>
                </div>
            )}

            {/* Interview List */}
            {isLoading ? (
                <div className="flex items-center justify-center py-20">
                    <Loader2 className="h-8 w-8 animate-spin text-primary" />
                </div>
            ) : interviews.length === 0 ? (
                <div className="text-center py-20 bg-muted/30 rounded-3xl border border-dashed border-muted-foreground/20">
                    <Video className="h-12 w-12 mx-auto text-muted-foreground mb-4 opacity-50" />
                    <h3 className="text-xl font-bold">No sessions practiced yet</h3>
                    <p className="text-muted-foreground mb-6">Launch your first AI mock interview to sharpen your skills.</p>
                    <Button size="lg" onClick={() => router.push('/interviews/new')} className="rounded-xl shadow-xl shadow-primary/20">
                        <Plus className="mr-2 h-5 w-5" /> Start Practice Interview
                    </Button>
                </div>
            ) : (
                <div className="space-y-4">
                    {interviews.map((interview) => (
                        <Card key={interview.id} className="hover:shadow-md transition-shadow">
                            <CardContent className="flex items-center justify-between py-4">
                                <div className="flex items-center gap-4">
                                    <div className="h-12 w-12 rounded-full bg-primary/10 flex items-center justify-center">
                                        <Video className="h-6 w-6 text-primary" />
                                    </div>
                                    <div>
                                        <h3 className="font-medium">{interview.role}</h3>
                                        <div className="flex items-center gap-2 text-sm text-muted-foreground">
                                            <span>{interview.domain}</span>
                                            {interview.company && <span> {interview.company}</span>}
                                            <span> {interview.difficulty}</span>
                                        </div>
                                    </div>
                                </div>
                                <div className="flex items-center gap-4">
                                    <span className={`inline-flex items-center gap-1 text-xs px-2 py-1 rounded-full ${getStatusColor(interview.status)}`}>
                                        {getStatusIcon(interview.status)}
                                        {interview.status}
                                    </span>
                                    {interview.score !== undefined && interview.score !== null && (
                                        <span className="text-sm font-medium">{interview.score}%</span>
                                    )}
                                    {interview.status === 'SCHEDULED' && (
                                        <Button size="sm" onClick={() => handleStartInterview(interview.id)}>
                                            Start
                                        </Button>
                                    )}
                                    {interview.status === 'COMPLETED' && (
                                        <Button size="sm" variant="outline" onClick={() => router.push(`/interviews/${interview.id}/report`)}>
                                            View Report
                                        </Button>
                                    )}
                                </div>
                            </CardContent>
                        </Card>
                    ))}
                </div>
            )
            }
        </div >
    )
}

"use client"

import * as React from 'react'
import Link from 'next/link'
import Image from 'next/image'
import { useRouter } from 'next/navigation'
import { useAuth } from '@/lib/auth-context'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Eye, EyeOff, Loader2 } from 'lucide-react'

export default function LoginPage() {
    const router = useRouter()
    const { login, isAuthenticated } = useAuth()

    const [email, setEmail] = React.useState('')
    const [password, setPassword] = React.useState('')
    const [showPassword, setShowPassword] = React.useState(false)
    const [isLoading, setIsLoading] = React.useState(false)
    const [error, setError] = React.useState('')

    React.useEffect(() => {
        if (isAuthenticated) {
            router.push('/dashboard')
        }
    }, [isAuthenticated, router])

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault()
        setError('')
        setIsLoading(true)

        try {
            await login(email, password)
            router.push('/dashboard')
        } catch (err: any) {
            setError(err.response?.data?.error || 'Login failed. Please try again.')
        } finally {
            setIsLoading(false)
        }
    }

    return (
        <div className="min-h-[calc(100vh-4rem)] flex items-center justify-center px-4 py-12 bg-gradient-to-br from-background via-background to-primary/5">
            <div className="w-full max-w-md space-y-6">
                <div className="flex justify-center">
                    <Link href="/">
                        <Image src="/logo-light.png" alt="TechWell" width={160} height={48} className="dark:hidden" priority />
                        <Image src="/logo-dark.png" alt="TechWell" width={160} height={48} className="hidden dark:block" priority />
                    </Link>
                </div>
                <Card className="border-muted shadow-xl">
                    <CardHeader className="text-center">
                        <CardTitle className="text-2xl font-bold">Welcome Back</CardTitle>
                        <CardDescription>Sign in to your TechWell account</CardDescription>
                    </CardHeader>
                    <CardContent>
                        <form onSubmit={handleSubmit} className="space-y-4">
                            {error && (
                                <div className="p-3 text-sm text-red-500 bg-red-50 dark:bg-red-950/20 rounded-lg">
                                    {error}
                                </div>
                            )}

                            <div className="space-y-2">
                                <label htmlFor="email" className="text-sm font-medium">
                                    Email
                                </label>
                                <Input
                                    id="email"
                                    type="email"
                                    placeholder="you@example.com"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    required
                                    disabled={isLoading}
                                />
                            </div>

                            <div className="space-y-2">
                                <label htmlFor="password" className="text-sm font-medium">
                                    Password
                                </label>
                                <div className="relative">
                                    <Input
                                        id="password"
                                        type={showPassword ? 'text' : 'password'}
                                        placeholder=""
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        required
                                        disabled={isLoading}
                                        className="pr-10"
                                    />
                                    <button
                                        type="button"
                                        onClick={() => setShowPassword(!showPassword)}
                                        className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground"
                                    >
                                        {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                                    </button>
                                </div>
                            </div>

                            <Button type="submit" className="w-full" disabled={isLoading}>
                                {isLoading ? (
                                    <>
                                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                                        Signing in...
                                    </>
                                ) : (
                                    'Sign In'
                                )}
                            </Button>
                        </form>

                        <div className="mt-6 text-center text-sm">
                            <span className="text-muted-foreground">Don&apos;t have an account? </span>
                            <Link href="/register" className="text-primary hover:underline font-medium">
                                Sign up
                            </Link>
                        </div>
                    </CardContent>
                </Card>
            </div>
        </div>
    )
}

"use client"

import * as React from 'react'
import { useParams } from 'next/navigation'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import {
    Award,
    Video,
    BookOpen,
    Share2,
    Linkedin,
    Twitter,
    Link2,
    Download,
    ExternalLink,
    CheckCircle,
    Star,
    Calendar,
    Copy
} from 'lucide-react'
import api from '@/lib/api'

interface PortfolioData {
    user: {
        id: string
        name: string
        headline?: string
        avatarUrl?: string
        linkedinUrl?: string
        portfolioPublic: boolean
    }
    certificates: {
        id: string
        uniqueId: string
        courseName: string
        courseCategory?: string
        issueDate: string
        grade?: string
        isPublic: boolean
    }[]
    interviewAchievements: {
        id: string
        domain: string
        role: string
        score: number
        completedAt: string
        isPublic: boolean
    }[]
    stats: {
        totalCertificates: number
        totalInterviews: number
        averageScore: number
    }
}

export default function PublicPortfolioPage() {
    const params = useParams()
    const username = params.username as string

    const [portfolio, setPortfolio] = React.useState<PortfolioData | null>(null)
    const [isLoading, setIsLoading] = React.useState(true)
    const [error, setError] = React.useState<string | null>(null)
    const [copiedLink, setCopiedLink] = React.useState(false)

    React.useEffect(() => {
        fetchPortfolio()
    }, [username])

    const fetchPortfolio = async () => {
        try {
            const res = await api.get(`/portfolio/${username}`)
            setPortfolio(res.data)
        } catch (err: any) {
            setError(err.response?.status === 404 ? 'Portfolio not found' : 'Failed to load portfolio')
            // Mock data for demo
            setPortfolio({
                user: {
                    id: '1',
                    name: 'John Doe',
                    headline: 'Full Stack Developer | React | Node.js',
                    portfolioPublic: true
                },
                certificates: [
                    { id: '1', uniqueId: 'CERT-2026-00001', courseName: 'Advanced JavaScript', courseCategory: 'Development', issueDate: '2026-01-15', grade: 'A', isPublic: true },
                    { id: '2', uniqueId: 'CERT-2026-00002', courseName: 'React Masterclass', courseCategory: 'Development', issueDate: '2026-01-20', grade: 'A+', isPublic: true }
                ],
                interviewAchievements: [
                    { id: '1', domain: 'Frontend', role: 'Senior React Developer', score: 92, completedAt: '2026-01-25', isPublic: true },
                    { id: '2', domain: 'Backend', role: 'Node.js Developer', score: 88, completedAt: '2026-01-28', isPublic: true }
                ],
                stats: { totalCertificates: 2, totalInterviews: 2, averageScore: 90 }
            })
        } finally {
            setIsLoading(false)
        }
    }

    const getPortfolioUrl = () => {
        if (typeof window !== 'undefined') {
            return `${window.location.origin}/portfolio/${username}`
        }
        return ''
    }

    const handleCopyLink = async () => {
        await navigator.clipboard.writeText(getPortfolioUrl())
        setCopiedLink(true)
        setTimeout(() => setCopiedLink(false), 2000)
    }

    const handleShareLinkedIn = () => {
        const url = encodeURIComponent(getPortfolioUrl())
        const title = encodeURIComponent(`Check out my TechWell Portfolio`)
        window.open(`https://www.linkedin.com/sharing/share-offsite/?url=${url}`, '_blank')
    }

    const handleShareTwitter = () => {
        const url = encodeURIComponent(getPortfolioUrl())
        const text = encodeURIComponent(`Check out my professional portfolio with certificates and interview achievements! `)
        window.open(`https://twitter.com/intent/tweet?url=${url}&text=${text}`, '_blank')
    }

    const handleShareCertificateLinkedIn = (cert: PortfolioData['certificates'][0]) => {
        const certUrl = encodeURIComponent(`${getPortfolioUrl()}/certificate/${cert.uniqueId}`)
        const title = encodeURIComponent(`Certificate: ${cert.courseName}`)
        window.open(`https://www.linkedin.com/profile/add?startTask=CERTIFICATION_NAME&name=${encodeURIComponent(cert.courseName)}&organizationName=TechWell&issueYear=2026&issueMonth=1&certUrl=${certUrl}&certId=${cert.uniqueId}`, '_blank')
    }

    if (isLoading) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <div className="animate-spin h-8 w-8 border-4 border-primary border-t-transparent rounded-full" />
            </div>
        )
    }

    if (!portfolio) {
        return (
            <div className="min-h-screen flex flex-col items-center justify-center">
                <h1 className="text-2xl font-bold mb-2">Portfolio Not Found</h1>
                <p className="text-muted-foreground">This portfolio doesn&apos;t exist or is private.</p>
            </div>
        )
    }

    return (
        <div className="min-h-screen bg-gradient-to-b from-background to-muted/30">
            {/* Header */}
            <div className="bg-gradient-to-r from-primary/10 via-primary/5 to-background pt-12 pb-20">
                <div className="container max-w-4xl">
                    <div className="flex flex-col md:flex-row items-center gap-6">
                        {/* Avatar */}
                        <div className="w-24 h-24 rounded-full bg-gradient-to-br from-primary to-primary/60 flex items-center justify-center text-3xl font-bold text-white">
                            {portfolio.user.name.charAt(0)}
                        </div>

                        {/* Info */}
                        <div className="text-center md:text-left flex-1">
                            <h1 className="text-3xl font-bold">{portfolio.user.name}</h1>
                            {portfolio.user.headline && (
                                <p className="text-muted-foreground mt-1">{portfolio.user.headline}</p>
                            )}
                        </div>

                        {/* Share Buttons */}
                        <div className="flex gap-2">
                            <Button variant="outline" size="sm" onClick={handleCopyLink}>
                                {copiedLink ? <CheckCircle className="h-4 w-4 mr-2" /> : <Copy className="h-4 w-4 mr-2" />}
                                {copiedLink ? 'Copied!' : 'Copy Link'}
                            </Button>
                            <Button variant="outline" size="icon" onClick={handleShareLinkedIn} title="Share on LinkedIn">
                                <Linkedin className="h-4 w-4" />
                            </Button>
                            <Button variant="outline" size="icon" onClick={handleShareTwitter} title="Share on Twitter">
                                <Twitter className="h-4 w-4" />
                            </Button>
                        </div>
                    </div>

                    {/* Stats */}
                    <div className="grid grid-cols-3 gap-4 mt-8">
                        <Card className="bg-white/50 backdrop-blur">
                            <CardContent className="pt-4 text-center">
                                <p className="text-3xl font-bold text-primary">{portfolio.stats.totalCertificates}</p>
                                <p className="text-sm text-muted-foreground">Certificates</p>
                            </CardContent>
                        </Card>
                        <Card className="bg-white/50 backdrop-blur">
                            <CardContent className="pt-4 text-center">
                                <p className="text-3xl font-bold text-green-600">{portfolio.stats.totalInterviews}</p>
                                <p className="text-sm text-muted-foreground">AI Interviews</p>
                            </CardContent>
                        </Card>
                        <Card className="bg-white/50 backdrop-blur">
                            <CardContent className="pt-4 text-center">
                                <p className="text-3xl font-bold text-orange-600">{portfolio.stats.averageScore}%</p>
                                <p className="text-sm text-muted-foreground">Avg Score</p>
                            </CardContent>
                        </Card>
                    </div>
                </div>
            </div>

            {/* Content */}
            <div className="container max-w-4xl -mt-8 pb-12">
                {/* Certificates */}
                <Card className="mb-6">
                    <CardHeader>
                        <CardTitle className="flex items-center gap-2">
                            <Award className="h-5 w-5 text-primary" />
                            Certificates
                        </CardTitle>
                        <CardDescription>Verified course completion certificates</CardDescription>
                    </CardHeader>
                    <CardContent>
                        {portfolio.certificates.length > 0 ? (
                            <div className="space-y-4">
                                {portfolio.certificates.map(cert => (
                                    <div key={cert.id} className="flex items-center justify-between p-4 border rounded-lg hover:bg-muted/50 transition-colors">
                                        <div className="flex items-center gap-4">
                                            <div className="h-12 w-12 rounded-lg bg-primary/10 flex items-center justify-center">
                                                <Award className="h-6 w-6 text-primary" />
                                            </div>
                                            <div>
                                                <h4 className="font-medium">{cert.courseName}</h4>
                                                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                                                    <Calendar className="h-3 w-3" />
                                                    {new Date(cert.issueDate).toLocaleDateString()}
                                                    {cert.grade && (
                                                        <>
                                                            <span></span>
                                                            <Badge variant="secondary">Grade: {cert.grade}</Badge>
                                                        </>
                                                    )}
                                                </div>
                                            </div>
                                        </div>
                                        <div className="flex gap-2">
                                            <Button
                                                variant="ghost"
                                                size="sm"
                                                onClick={() => window.open(`/verify/${cert.uniqueId}`, '_blank')}
                                            >
                                                <ExternalLink className="h-4 w-4 mr-1" />
                                                Verify
                                            </Button>
                                            <Button
                                                variant="outline"
                                                size="sm"
                                                onClick={() => handleShareCertificateLinkedIn(cert)}
                                            >
                                                <Linkedin className="h-4 w-4 mr-1" />
                                                Add to LinkedIn
                                            </Button>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <p className="text-center text-muted-foreground py-8">No certificates yet</p>
                        )}
                    </CardContent>
                </Card>

                {/* Interview Achievements */}
                <Card>
                    <CardHeader>
                        <CardTitle className="flex items-center gap-2">
                            <Video className="h-5 w-5 text-green-600" />
                            AI Interview Achievements
                        </CardTitle>
                        <CardDescription>Performance in AI-powered mock interviews</CardDescription>
                    </CardHeader>
                    <CardContent>
                        {portfolio.interviewAchievements.length > 0 ? (
                            <div className="space-y-4">
                                {portfolio.interviewAchievements.map(interview => (
                                    <div key={interview.id} className="flex items-center justify-between p-4 border rounded-lg hover:bg-muted/50 transition-colors">
                                        <div className="flex items-center gap-4">
                                            <div className={`h-12 w-12 rounded-lg flex items-center justify-center ${interview.score >= 90 ? 'bg-green-100 text-green-600' :
                                                interview.score >= 70 ? 'bg-yellow-100 text-yellow-600' :
                                                    'bg-orange-100 text-orange-600'
                                                }`}>
                                                <span className="font-bold">{interview.score}%</span>
                                            </div>
                                            <div>
                                                <h4 className="font-medium">{interview.role}</h4>
                                                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                                                    <Badge variant="outline">{interview.domain}</Badge>
                                                    <span></span>
                                                    <Calendar className="h-3 w-3" />
                                                    {new Date(interview.completedAt).toLocaleDateString()}
                                                </div>
                                            </div>
                                        </div>
                                        <div className="flex items-center gap-2">
                                            {interview.score >= 85 && (
                                                <Badge className="bg-gradient-to-r from-yellow-500 to-orange-500 text-white">
                                                    <Star className="h-3 w-3 mr-1" />
                                                    Top Performer
                                                </Badge>
                                            )}
                                            <Button
                                                variant="outline"
                                                size="sm"
                                                onClick={() => {
                                                    const url = encodeURIComponent(`${getPortfolioUrl()}/interview/${interview.id}`)
                                                    const text = encodeURIComponent(`I scored ${interview.score}% in a ${interview.role} AI Interview! `)
                                                    window.open(`https://www.linkedin.com/sharing/share-offsite/?url=${url}`, '_blank')
                                                }}
                                            >
                                                <Share2 className="h-4 w-4 mr-1" />
                                                Share
                                            </Button>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <p className="text-center text-muted-foreground py-8">No interview achievements yet</p>
                        )}
                    </CardContent>
                </Card>

                {/* Add to Resume */}
                <div className="mt-8 p-6 bg-gradient-to-r from-primary/5 to-primary/10 rounded-lg border">
                    <h3 className="font-semibold mb-2 flex items-center gap-2">
                        <BookOpen className="h-5 w-5" />
                        Add to Your Resume
                    </h3>
                    <p className="text-sm text-muted-foreground mb-4">
                        Share your verified achievements by adding this portfolio link to your resume or LinkedIn profile.
                    </p>
                    <div className="flex items-center gap-2 p-3 bg-white rounded-lg border">
                        <code className="flex-1 text-sm text-primary">{getPortfolioUrl()}</code>
                        <Button size="sm" onClick={handleCopyLink}>
                            {copiedLink ? 'Copied!' : 'Copy'}
                        </Button>
                    </div>
                </div>
            </div>
        </div>
    )
}

"use client"

import * as React from 'react'
import { useRouter } from 'next/navigation'
import { useAuth } from '@/lib/auth-context'
import { userApi } from '@/lib/api'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { User as UserIcon, Mail, Phone, Save, Loader2, Shield } from 'lucide-react'

interface ExtendedUser {
    id: string
    name: string
    email: string
    role: string
    phone?: string
    avatar?: string
}

export default function ProfilePage() {
    const router = useRouter()
    const { user, isAuthenticated, isLoading: authLoading, refreshUser, logout } = useAuth()
    const currentUser = user as ExtendedUser | null

    const [isEditing, setIsEditing] = React.useState(false)
    const [isSaving, setIsSaving] = React.useState(false)
    const [formData, setFormData] = React.useState({
        name: '',
        phone: '',
    })
    const [message, setMessage] = React.useState<{ type: 'success' | 'error'; text: string } | null>(null)

    React.useEffect(() => {
        if (!authLoading && !isAuthenticated) {
            router.push('/login')
        }
    }, [authLoading, isAuthenticated, router])

    React.useEffect(() => {
        if (currentUser) {
            setFormData({
                name: currentUser.name || '',
                phone: currentUser.phone || '',
            })
        }
    }, [currentUser])

    const handleSave = async (e: React.FormEvent) => {
        e.preventDefault()
        setIsSaving(true)
        setMessage(null)

        try {
            await userApi.updateMe(formData)
            await refreshUser()
            setIsEditing(false)
            setMessage({ type: 'success', text: 'Profile updated successfully!' })
        } catch (error: unknown) {
            const err = error as { response?: { data?: { error?: string } } }
            setMessage({ type: 'error', text: err.response?.data?.error || 'Failed to update profile' })
        } finally {
            setIsSaving(false)
        }
    }

    const getRoleBadgeColor = (role: string) => {
        switch (role) {
            case 'SUPER_ADMIN': return 'bg-red-100 text-red-700 dark:bg-red-900/30 dark:text-red-400'
            case 'ADMIN': return 'bg-purple-100 text-purple-700 dark:bg-purple-900/30 dark:text-purple-400'
            case 'INSTRUCTOR': return 'bg-blue-100 text-blue-700 dark:bg-blue-900/30 dark:text-blue-400'
            default: return 'bg-green-100 text-green-700 dark:bg-green-900/30 dark:text-green-400'
        }
    }

    if (authLoading || !currentUser) {
        return (
            <div className="min-h-[calc(100vh-4rem)] flex items-center justify-center">
                <Loader2 className="h-8 w-8 animate-spin text-primary" />
            </div>
        )
    }

    return (
        <div className="container py-8 max-w-2xl">
            <h1 className="text-3xl font-bold mb-8">My Profile</h1>

            {message && (
                <div className={`mb-6 p-3 rounded-lg text-sm ${message.type === 'success'
                    ? 'bg-green-50 text-green-700 dark:bg-green-900/20 dark:text-green-400'
                    : 'bg-red-50 text-red-700 dark:bg-red-900/20 dark:text-red-400'
                    }`}>
                    {message.text}
                </div>
            )}

            <Card>
                <CardHeader>
                    <div className="flex items-center justify-between">
                        <div className="flex items-center gap-4">
                            <div className="h-16 w-16 rounded-full bg-primary/10 flex items-center justify-center">
                                <UserIcon className="h-8 w-8 text-primary" />
                            </div>
                            <div>
                                <CardTitle>{currentUser.name}</CardTitle>
                                <CardDescription className="flex items-center gap-2">
                                    <Mail className="h-3 w-3" />
                                    {currentUser.email}
                                </CardDescription>
                            </div>
                        </div>
                        <span className={`inline-flex items-center gap-1 text-xs px-3 py-1 rounded-full ${getRoleBadgeColor(currentUser.role)}`}>
                            <Shield className="h-3 w-3" />
                            {currentUser.role}
                        </span>
                    </div>
                </CardHeader>
                <CardContent>
                    {isEditing ? (
                        <form onSubmit={handleSave} className="space-y-4">
                            <div className="space-y-2">
                                <label className="text-sm font-medium">Full Name</label>
                                <Input
                                    value={formData.name}
                                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                                    required
                                />
                            </div>
                            <div className="space-y-2">
                                <label className="text-sm font-medium">Phone Number</label>
                                <Input
                                    value={formData.phone}
                                    onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                                    placeholder="+91 9876543210"
                                />
                            </div>
                            <div className="flex gap-2">
                                <Button type="submit" disabled={isSaving}>
                                    {isSaving ? (
                                        <>
                                            <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                                            Saving...
                                        </>
                                    ) : (
                                        <>
                                            <Save className="mr-2 h-4 w-4" />
                                            Save Changes
                                        </>
                                    )}
                                </Button>
                                <Button type="button" variant="outline" onClick={() => setIsEditing(false)}>
                                    Cancel
                                </Button>
                            </div>
                        </form>
                    ) : (
                        <div className="space-y-4">
                            <div className="grid grid-cols-2 gap-4">
                                <div>
                                    <label className="text-sm font-medium text-muted-foreground">Full Name</label>
                                    <p className="mt-1">{currentUser.name}</p>
                                </div>
                                <div>
                                    <label className="text-sm font-medium text-muted-foreground">Email</label>
                                    <p className="mt-1">{currentUser.email}</p>
                                </div>
                                <div>
                                    <label className="text-sm font-medium text-muted-foreground">Phone</label>
                                    <p className="mt-1">{currentUser.phone || 'Not set'}</p>
                                </div>
                                <div>
                                    <label className="text-sm font-medium text-muted-foreground">Role</label>
                                    <p className="mt-1">{currentUser.role}</p>
                                </div>
                            </div>
                            <div className="flex gap-2 pt-4">
                                <Button onClick={() => setIsEditing(true)}>
                                    Edit Profile
                                </Button>
                                <Button variant="destructive" onClick={logout}>
                                    Logout
                                </Button>
                            </div>
                        </div>
                    )}
                </CardContent>
            </Card>
        </div>
    )
}

"use client"

import * as React from 'react'
import { useState, useEffect } from 'react'
import { useRouter } from 'next/navigation'
import { useAuth } from '@/lib/auth-context'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Textarea } from '@/components/ui/textarea'
import { Label } from '@/components/ui/label'
import { Switch } from '@/components/ui/switch'
import { Badge } from '@/components/ui/badge'
import {
    Share2,
    ExternalLink,
    Linkedin,
    Copy,
    CheckCircle,
    Globe,
    Award,
    Video,
    Eye,
    Save,
    Loader2
} from 'lucide-react'
import api from '@/lib/api'

export default function PortfolioSettingsPage() {
    const router = useRouter()
    const { user, isLoading: authLoading } = useAuth()

    const [settings, setSettings] = useState({
        portfolioPublic: true,
        showCertificates: true,
        showInterviews: true,
        headline: '',
        linkedinUrl: ''
    })
    const [isSaving, setIsSaving] = useState(false)
    const [copiedLink, setCopiedLink] = useState(false)

    const getPortfolioUrl = () => {
        if (typeof window !== 'undefined' && user) {
            return `${window.location.origin}/portfolio/${user.id}`
        }
        return ''
    }

    const handleCopyLink = async () => {
        await navigator.clipboard.writeText(getPortfolioUrl())
        setCopiedLink(true)
        setTimeout(() => setCopiedLink(false), 2000)
    }

    const handleSave = async () => {
        setIsSaving(true)
        try {
            await api.put('/portfolio/me/settings', settings)
            alert('Settings saved!')
        } catch (error) {
            console.error('Failed to save settings:', error)
            alert('Settings saved (mock)')
        } finally {
            setIsSaving(false)
        }
    }

    useEffect(() => {
        const loadSettings = async () => {
            try {
                const res = await api.get('/portfolio/me/settings')
                setSettings(prev => ({ ...prev, ...res.data }))
            } catch (error) {
                console.error('Failed to load settings:', error)
            }
        }
        if (user) {
            loadSettings()
        }
    }, [user])

    if (authLoading) {
        return (
            <div className="min-h-screen flex items-center justify-center">
                <Loader2 className="h-8 w-8 animate-spin" />
            </div>
        )
    }

    if (!user) {
        router.push('/login')
        return null
    }

    return (
        <div className="container max-w-3xl py-8">
            <div className="mb-8">
                <h1 className="text-3xl font-bold">Portfolio Settings</h1>
                <p className="text-muted-foreground">Manage your public profile and shareable achievements.</p>
            </div>

            {/* Portfolio Link */}
            <Card className="mb-6">
                <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                        <Share2 className="h-5 w-5" />
                        Your Portfolio Link
                    </CardTitle>
                    <CardDescription>Share this URL on your resume or LinkedIn profile</CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                    <div className="flex items-center gap-2 p-3 bg-muted rounded-lg">
                        <Globe className="h-4 w-4 text-muted-foreground" />
                        <code className="flex-1 text-sm">{getPortfolioUrl()}</code>
                        <Button size="sm" onClick={handleCopyLink}>
                            {copiedLink ? <CheckCircle className="h-4 w-4" /> : <Copy className="h-4 w-4" />}
                        </Button>
                    </div>
                    <div className="flex gap-2">
                        <Button variant="outline" onClick={() => window.open(getPortfolioUrl(), '_blank')}>
                            <Eye className="h-4 w-4 mr-2" />
                            Preview Portfolio
                        </Button>
                        <Button variant="outline" onClick={() => {
                            const url = encodeURIComponent(getPortfolioUrl())
                            window.open(`https://www.linkedin.com/sharing/share-offsite/?url=${url}`, '_blank')
                        }}>
                            <Linkedin className="h-4 w-4 mr-2" />
                            Share on LinkedIn
                        </Button>
                    </div>
                </CardContent>
            </Card>

            {/* Visibility Settings */}
            <Card className="mb-6">
                <CardHeader>
                    <CardTitle>Visibility Settings</CardTitle>
                    <CardDescription>Control what appears on your public portfolio</CardDescription>
                </CardHeader>
                <CardContent className="space-y-6">
                    <div className="flex items-center justify-between">
                        <div className="space-y-0.5">
                            <Label>Make Portfolio Public</Label>
                            <p className="text-sm text-muted-foreground">Allow anyone with the link to view your portfolio</p>
                        </div>
                        <Switch
                            checked={settings.portfolioPublic}
                            onCheckedChange={(checked) => setSettings(prev => ({ ...prev, portfolioPublic: checked }))}
                        />
                    </div>
                    <div className="flex items-center justify-between">
                        <div className="flex items-center gap-2">
                            <Award className="h-5 w-5 text-primary" />
                            <div className="space-y-0.5">
                                <Label>Show Certificates</Label>
                                <p className="text-sm text-muted-foreground">Display your course completion certificates</p>
                            </div>
                        </div>
                        <Switch
                            checked={settings.showCertificates}
                            onCheckedChange={(checked) => setSettings(prev => ({ ...prev, showCertificates: checked }))}
                        />
                    </div>
                    <div className="flex items-center justify-between">
                        <div className="flex items-center gap-2">
                            <Video className="h-5 w-5 text-green-600" />
                            <div className="space-y-0.5">
                                <Label>Show Interview Results</Label>
                                <p className="text-sm text-muted-foreground">Display your AI interview achievements and scores</p>
                            </div>
                        </div>
                        <Switch
                            checked={settings.showInterviews}
                            onCheckedChange={(checked) => setSettings(prev => ({ ...prev, showInterviews: checked }))}
                        />
                    </div>
                </CardContent>
            </Card>

            {/* Profile Info */}
            <Card className="mb-6">
                <CardHeader>
                    <CardTitle>Profile Information</CardTitle>
                    <CardDescription>Additional info displayed on your portfolio</CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                    <div className="space-y-2">
                        <Label htmlFor="headline">Professional Headline</Label>
                        <Input
                            id="headline"
                            placeholder="e.g., Full Stack Developer | React | Node.js"
                            value={settings.headline}
                            onChange={(e) => setSettings(prev => ({ ...prev, headline: e.target.value }))}
                        />
                    </div>
                    <div className="space-y-2">
                        <Label htmlFor="linkedin">LinkedIn Profile URL</Label>
                        <Input
                            id="linkedin"
                            placeholder="https://linkedin.com/in/yourprofile"
                            value={settings.linkedinUrl}
                            onChange={(e) => setSettings(prev => ({ ...prev, linkedinUrl: e.target.value }))}
                        />
                    </div>
                </CardContent>
            </Card>

            {/* Save Button */}
            <Button onClick={handleSave} disabled={isSaving} className="w-full">
                {isSaving ? (
                    <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                ) : (
                    <Save className="h-4 w-4 mr-2" />
                )}
                Save Settings
            </Button>
        </div>
    )
}

"use client"

import * as React from 'react'
import Link from 'next/link'
import Image from 'next/image'
import { useRouter } from 'next/navigation'
import { useAuth } from '@/lib/auth-context'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Eye, EyeOff, Loader2, Check, X } from 'lucide-react'

export default function RegisterPage() {
    const router = useRouter()
    const { register, isAuthenticated } = useAuth()

    const [name, setName] = React.useState('')
    const [email, setEmail] = React.useState('')
    const [password, setPassword] = React.useState('')
    const [confirmPassword, setConfirmPassword] = React.useState('')
    const [showPassword, setShowPassword] = React.useState(false)
    const [isLoading, setIsLoading] = React.useState(false)
    const [error, setError] = React.useState('')

    React.useEffect(() => {
        if (isAuthenticated) {
            router.push('/dashboard')
        }
    }, [isAuthenticated, router])

    const passwordChecks = {
        length: password.length >= 8,
        match: password === confirmPassword && confirmPassword.length > 0,
    }

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault()
        setError('')

        if (!passwordChecks.length) {
            setError('Password must be at least 8 characters')
            return
        }

        if (!passwordChecks.match) {
            setError('Passwords do not match')
            return
        }

        setIsLoading(true)

        try {
            await register(email, password, name)
            router.push('/dashboard')
        } catch (err: any) {
            setError(err.response?.data?.error || 'Registration failed. Please try again.')
        } finally {
            setIsLoading(false)
        }
    }

    return (
        <div className="min-h-[calc(100vh-4rem)] flex items-center justify-center px-4 py-12 bg-gradient-to-br from-background via-background to-primary/5">
            <div className="w-full max-w-md space-y-6">
                <div className="flex justify-center">
                    <Link href="/">
                        <Image src="/logo-light.png" alt="TechWell" width={160} height={48} className="dark:hidden" priority />
                        <Image src="/logo-dark.png" alt="TechWell" width={160} height={48} className="hidden dark:block" priority />
                    </Link>
                </div>
                <Card className="border-muted shadow-xl">
                    <CardHeader className="text-center">
                        <CardTitle className="text-2xl font-bold">Create Account</CardTitle>
                        <CardDescription>Join TechWell and start your journey</CardDescription>
                    </CardHeader>
                    <CardContent>
                        <form onSubmit={handleSubmit} className="space-y-4">
                            {error && (
                                <div className="p-3 text-sm text-red-500 bg-red-50 dark:bg-red-950/20 rounded-lg">
                                    {error}
                                </div>
                            )}

                            <div className="space-y-2">
                                <label htmlFor="name" className="text-sm font-medium">
                                    Full Name
                                </label>
                                <Input
                                    id="name"
                                    type="text"
                                    placeholder="John Doe"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}
                                    required
                                    disabled={isLoading}
                                />
                            </div>

                            <div className="space-y-2">
                                <label htmlFor="email" className="text-sm font-medium">
                                    Email
                                </label>
                                <Input
                                    id="email"
                                    type="email"
                                    placeholder="you@example.com"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    required
                                    disabled={isLoading}
                                />
                            </div>

                            <div className="space-y-2">
                                <label htmlFor="password" className="text-sm font-medium">
                                    Password
                                </label>
                                <div className="relative">
                                    <Input
                                        id="password"
                                        type={showPassword ? 'text' : 'password'}
                                        placeholder=""
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                        required
                                        disabled={isLoading}
                                        className="pr-10"
                                    />
                                    <button
                                        type="button"
                                        onClick={() => setShowPassword(!showPassword)}
                                        className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground"
                                    >
                                        {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                                    </button>
                                </div>
                                <div className="flex items-center gap-2 text-xs">
                                    {passwordChecks.length ? (
                                        <Check className="h-3 w-3 text-green-500" />
                                    ) : (
                                        <X className="h-3 w-3 text-muted-foreground" />
                                    )}
                                    <span className={passwordChecks.length ? 'text-green-500' : 'text-muted-foreground'}>
                                        At least 8 characters
                                    </span>
                                </div>
                            </div>

                            <div className="space-y-2">
                                <label htmlFor="confirmPassword" className="text-sm font-medium">
                                    Confirm Password
                                </label>
                                <Input
                                    id="confirmPassword"
                                    type={showPassword ? 'text' : 'password'}
                                    placeholder=""
                                    value={confirmPassword}
                                    onChange={(e) => setConfirmPassword(e.target.value)}
                                    required
                                    disabled={isLoading}
                                />
                                {confirmPassword && (
                                    <div className="flex items-center gap-2 text-xs">
                                        {passwordChecks.match ? (
                                            <Check className="h-3 w-3 text-green-500" />
                                        ) : (
                                            <X className="h-3 w-3 text-red-500" />
                                        )}
                                        <span className={passwordChecks.match ? 'text-green-500' : 'text-red-500'}>
                                            {passwordChecks.match ? 'Passwords match' : 'Passwords do not match'}
                                        </span>
                                    </div>
                                )}
                            </div>

                            <Button type="submit" className="w-full" disabled={isLoading}>
                                {isLoading ? (
                                    <>
                                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                                        Creating account...
                                    </>
                                ) : (
                                    'Create Account'
                                )}
                            </Button>
                        </form>

                        <div className="mt-6 text-center text-sm">
                            <span className="text-muted-foreground">Already have an account? </span>
                            <Link href="/login" className="text-primary hover:underline font-medium">
                                Sign in
                            </Link>
                        </div>
                    </CardContent>
                </Card>
            </div>
        </div>
    )
}

