}

"use client"

import * as React from 'react'
import { useState, useRef, useCallback } from 'react'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card'
import { Progress } from '@/components/ui/progress'
import {
    Video,
    StopCircle,
    PlayCircle,
    Upload,
    ScreenShare,
    Camera,
    Mic,
    MicOff,
    Monitor,
    Trash2,
    Download,
    Loader2,
    AlertCircle
} from 'lucide-react'

interface VideoRecorderProps {
    onRecordingComplete?: (blob: Blob, thumbnail?: string) => void
    onUploadComplete?: (url: string, thumbnail?: string) => void
    maxDuration?: number // in seconds
    allowScreenShare?: boolean
}

type RecordingMode = 'camera' | 'screen' | 'screen_with_camera'

export function VideoRecorder({
    onRecordingComplete,
    onUploadComplete,
    maxDuration = 600, // 10 minutes default
    allowScreenShare = true
}: VideoRecorderProps) {
    const [isRecording, setIsRecording] = useState(false)
    const [isPreviewing, setIsPreviewing] = useState(false)
    const [recordedBlob, setRecordedBlob] = useState<Blob | null>(null)
    const [recordedUrl, setRecordedUrl] = useState<string | null>(null)
    const [duration, setDuration] = useState(0)
    const [mode, setMode] = useState<RecordingMode>('camera')
    const [isMuted, setIsMuted] = useState(false)
    const [isUploading, setIsUploading] = useState(false)
    const [error, setError] = useState<string | null>(null)

    const videoRef = useRef<HTMLVideoElement>(null)
    const previewRef = useRef<HTMLVideoElement>(null)
    const mediaRecorderRef = useRef<MediaRecorder | null>(null)
    const streamRef = useRef<MediaStream | null>(null)
    const chunksRef = useRef<Blob[]>([])
    const timerRef = useRef<NodeJS.Timeout | null>(null)

    const stopRecording = useCallback(() => {
        if (timerRef.current) {
            clearInterval(timerRef.current)
            timerRef.current = null
        }

        if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
            mediaRecorderRef.current.stop()
        }

        if (streamRef.current) {
            streamRef.current.getTracks().forEach(track => track.stop())
            streamRef.current = null
        }

        setIsRecording(false)
    }, [])

    const startRecording = useCallback(async () => {
        setError(null)
        chunksRef.current = []

        try {
            let stream: MediaStream

            if (mode === 'camera') {
                stream = await navigator.mediaDevices.getUserMedia({
                    video: { width: 1280, height: 720, facingMode: 'user' },
                    audio: true
                })
            } else if (mode === 'screen') {
                stream = await navigator.mediaDevices.getDisplayMedia({
                    video: { width: 1920, height: 1080 },
                    audio: true
                })
            } else {
                // Screen with camera overlay
                const screenStream = await navigator.mediaDevices.getDisplayMedia({
                    video: { width: 1920, height: 1080 },
                    audio: true
                })
                const cameraStream = await navigator.mediaDevices.getUserMedia({
                    video: { width: 320, height: 240 },
                    audio: true
                })

                // Combine streams
                const tracks = [
                    ...screenStream.getVideoTracks(),
                    ...cameraStream.getAudioTracks()
                ]
                stream = new MediaStream(tracks)
            }

            streamRef.current = stream

            if (videoRef.current) {
                videoRef.current.srcObject = stream
                videoRef.current.muted = true
                await videoRef.current.play()
            }

            const mediaRecorder = new MediaRecorder(stream, {
                mimeType: 'video/webm;codecs=vp9'
            })

            mediaRecorder.ondataavailable = (e) => {
                if (e.data.size > 0) {
                    chunksRef.current.push(e.data)
                }
            }

            mediaRecorder.onstop = () => {
                const blob = new Blob(chunksRef.current, { type: 'video/webm' })
                setRecordedBlob(blob)
                const url = URL.createObjectURL(blob)
                setRecordedUrl(url)
                setIsPreviewing(true)

                // Generate thumbnail
                if (videoRef.current) {
                    const canvas = document.createElement('canvas')
                    canvas.width = videoRef.current.videoWidth
                    canvas.height = videoRef.current.videoHeight
                    const ctx = canvas.getContext('2d')
                    if (ctx) {
                        ctx.drawImage(videoRef.current, 0, 0)
                        const thumbnail = canvas.toDataURL('image/jpeg', 0.7)
                        onRecordingComplete?.(blob, thumbnail)
                    }
                }
            }

            mediaRecorderRef.current = mediaRecorder
            mediaRecorder.start(1000) // Collect data every second
            setIsRecording(true)
            setDuration(0)

            // Start duration timer
            timerRef.current = setInterval(() => {
                setDuration(prev => {
                    if (prev >= maxDuration) {
                        stopRecording()
                        return prev
                    }
                    return prev + 1
                })
            }, 1000)

        } catch (err: any) {
            console.error('Failed to start recording:', err)
            setError(err.message || 'Failed to access camera/microphone')
        }
    }, [mode, maxDuration, onRecordingComplete, stopRecording])

    const discardRecording = useCallback(() => {
        if (recordedUrl) {
            URL.revokeObjectURL(recordedUrl)
        }
        setRecordedBlob(null)
        setRecordedUrl(null)
        setIsPreviewing(false)
        setDuration(0)
    }, [recordedUrl])

    const downloadRecording = useCallback(() => {
        if (!recordedBlob) return
        const url = URL.createObjectURL(recordedBlob)
        const a = document.createElement('a')
        a.href = url
        a.download = `recording_${Date.now()}.webm`
        a.click()
        URL.revokeObjectURL(url)
    }, [recordedBlob])

    const uploadRecording = useCallback(async () => {
        if (!recordedBlob) return
        setIsUploading(true)

        try {
            const formData = new FormData()
            formData.append('video', recordedBlob, 'recording.webm')

            // Mock upload - replace with actual API call
            await new Promise(resolve => setTimeout(resolve, 2000))
            const mockUrl = `https://storage.example.com/videos/${Date.now()}.webm`

            onUploadComplete?.(mockUrl)
            setIsUploading(false)
        } catch (err) {
            setError('Failed to upload recording')
            setIsUploading(false)
        }
    }, [recordedBlob, onUploadComplete])

    const toggleMute = useCallback(() => {
        if (streamRef.current) {
            streamRef.current.getAudioTracks().forEach(track => {
                track.enabled = isMuted
            })
        }
        setIsMuted(!isMuted)
    }, [isMuted])

    const formatTime = (seconds: number) => {
        const mins = Math.floor(seconds / 60)
        const secs = seconds % 60
        return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`
    }

    return (
        <Card>
            <CardHeader>
                <CardTitle className="flex items-center gap-2">
                    <Video className="h-5 w-5" />
                    Video Recorder
                </CardTitle>
                <CardDescription>
                    Record video from camera or capture your screen
                </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
                {error && (
                    <div className="p-3 bg-red-50 border border-red-200 rounded-lg flex items-center gap-2 text-red-700">
                        <AlertCircle className="h-4 w-4" />
                        <span className="text-sm">{error}</span>
                    </div>
                )}

                {/* Mode Selector */}
                {!isRecording && !isPreviewing && (
                    <div className="flex gap-2">
                        <Button
                            variant={mode === 'camera' ? 'default' : 'outline'}
                            size="sm"
                            onClick={() => setMode('camera')}
                        >
                            <Camera className="h-4 w-4 mr-2" />
                            Camera
                        </Button>
                        {allowScreenShare && (
                            <>
                                <Button
                                    variant={mode === 'screen' ? 'default' : 'outline'}
                                    size="sm"
                                    onClick={() => setMode('screen')}
                                >
                                    <Monitor className="h-4 w-4 mr-2" />
                                    Screen
                                </Button>
                                <Button
                                    variant={mode === 'screen_with_camera' ? 'default' : 'outline'}
                                    size="sm"
                                    onClick={() => setMode('screen_with_camera')}
                                >
                                    <ScreenShare className="h-4 w-4 mr-2" />
                                    Screen + Cam
                                </Button>
                            </>
                        )}
                    </div>
                )}

                {/* Video Preview */}
                <div className="relative aspect-video bg-black rounded-lg overflow-hidden">
                    {isPreviewing && recordedUrl ? (
                        <video
                            ref={previewRef}
                            src={recordedUrl}
                            controls
                            className="w-full h-full object-contain"
                        />
                    ) : (
                        <video
                            ref={videoRef}
                            className="w-full h-full object-contain"
                            autoPlay
                            muted
                            playsInline
                        />
                    )}

                    {/* Recording Indicator */}
                    {isRecording && (
                        <div className="absolute top-4 left-4 flex items-center gap-2 bg-red-600 text-white px-3 py-1 rounded-full">
                            <span className="w-2 h-2 bg-white rounded-full animate-pulse" />
                            <span className="text-sm font-medium">REC</span>
                            <span className="text-sm">{formatTime(duration)}</span>
                        </div>
                    )}

                    {/* Duration Progress */}
                    {isRecording && (
                        <div className="absolute bottom-0 left-0 right-0">
                            <Progress value={(duration / maxDuration) * 100} className="h-1 rounded-none" />
                        </div>
                    )}
                </div>

                {/* Controls */}
                <div className="flex items-center justify-center gap-3">
                    {!isRecording && !isPreviewing && (
                        <Button onClick={startRecording} className="gap-2">
                            <Video className="h-4 w-4" />
                            Start Recording
                        </Button>
                    )}

                    {isRecording && (
                        <>
                            <Button
                                variant="outline"
                                size="icon"
                                onClick={toggleMute}
                            >
                                {isMuted ? <MicOff className="h-4 w-4" /> : <Mic className="h-4 w-4" />}
                            </Button>
                            <Button
                                variant="destructive"
                                onClick={stopRecording}
                                className="gap-2"
                            >
                                <StopCircle className="h-4 w-4" />
                                Stop Recording
                            </Button>
                        </>
                    )}

                    {isPreviewing && (
                        <>
                            <Button
                                variant="outline"
                                onClick={discardRecording}
                                className="gap-2"
                            >
                                <Trash2 className="h-4 w-4" />
                                Discard
                            </Button>
                            <Button
                                variant="outline"
                                onClick={downloadRecording}
                                className="gap-2"
                            >
                                <Download className="h-4 w-4" />
                                Download
                            </Button>
                            <Button
                                onClick={uploadRecording}
                                disabled={isUploading}
                                className="gap-2"
                            >
                                {isUploading ? (
                                    <Loader2 className="h-4 w-4 animate-spin" />
                                ) : (
                                    <Upload className="h-4 w-4" />
                                )}
                                {isUploading ? 'Uploading...' : 'Use Recording'}
                            </Button>
                        </>
                    )}
                </div>

                {/* Duration Info */}
                {isRecording && (
                    <p className="text-center text-sm text-muted-foreground">
                        Max duration: {formatTime(maxDuration)}
                    </p>
                )}
            </CardContent>
        </Card>
    )
}

export default VideoRecorder

"use client"

import * as React from 'react'
import { Button } from '@/components/ui/button'
import { Progress } from '@/components/ui/progress' // Need to check if Progress exists, if not use custom
import { Upload, X, CheckCircle, FileVideo, Loader2 } from 'lucide-react'
import { uploadApi } from '@/lib/api'

interface VideoUploadProps {
    onUploadComplete: (url: string) => void
    initialUrl?: string
    label?: string
}

export function VideoUpload({ onUploadComplete, initialUrl, label = "Upload Video" }: VideoUploadProps) {
    const [file, setFile] = React.useState<File | null>(null)
    const [previewUrl, setPreviewUrl] = React.useState<string | null>(initialUrl || null)
    const [isUploading, setIsUploading] = React.useState(false)
    const [progress, setProgress] = React.useState(0)
    const [error, setError] = React.useState<string | null>(null)

    const fileInputRef = React.useRef<HTMLInputElement>(null)

    const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
        const selectedFile = e.target.files?.[0]
        if (!selectedFile) return

        // Validate type
        if (!selectedFile.type.startsWith('video/')) {
            setError('Please upload a valid video file')
            return
        }

        // Validate size (e.g. 50MB)
        if (selectedFile.size > 50 * 1024 * 1024) {
            setError('File size exceeds 50MB limit')
            return
        }

        setFile(selectedFile)
        setError(null)
        setProgress(0)
        setIsUploading(true)

        try {
            const formData = new FormData()
            formData.append('file', selectedFile)

            // Since axios handles progress, we could hook into it, 
            // but api wrapper might not expose onUploadProgress easily unless modified.
            // For now, fake progress or just wait.

            // Simulating progress for UX
            const interval = setInterval(() => {
                setProgress(prev => {
                    if (prev >= 90) {
                        clearInterval(interval)
                        return 90
                    }
                    return prev + 10
                })
            }, 300)

            const res = await uploadApi.upload(formData)

            clearInterval(interval)
            setProgress(100)

            // Backend returns relative URL like /uploads/filename.mp4
            // We need to prepend API URL if it's served from same domain or CDN
            // For this setup: http://localhost:5000/uploads/...
            // But frontend accesses /uploads via proxy or direct?
            // backend serves /uploads static route.
            const fullUrl = `${process.env.NEXT_PUBLIC_API_URL?.replace('/api', '')}${res.data.url}`

            setPreviewUrl(fullUrl)
            onUploadComplete(fullUrl)

        } catch (err: any) {
            console.error(err)
            setError(err.response?.data?.error || 'Upload failed')
            setFile(null)
        } finally {
            setIsUploading(false)
        }
    }

    const handleRemove = () => {
        setFile(null)
        setPreviewUrl(null)
        onUploadComplete('')
        if (fileInputRef.current) fileInputRef.current.value = ''
    }

    return (
        <div className="space-y-4">
            <label className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70">
                {label}
            </label>

            {!previewUrl ? (
                <div className="border-2 border-dashed rounded-lg p-6 hover:bg-muted/50 transition-colors cursor-pointer text-center"
                    onClick={() => fileInputRef.current?.click()}>
                    <input
                        type="file"
                        ref={fileInputRef}
                        className="hidden"
                        accept="video/*"
                        onChange={handleFileChange}
                    />

                    {isUploading ? (
                        <div className="space-y-2">
                            <Loader2 className="h-8 w-8 animate-spin mx-auto text-primary" />
                            <p className="text-sm text-muted-foreground">Uploading... {progress}%</p>
                            {/* Simple Progress Bar */}
                            <div className="h-2 bg-secondary rounded-full overflow-hidden">
                                <div className="h-full bg-primary transition-all duration-300" style={{ width: `${progress}%` }} />
                            </div>
                        </div>
                    ) : (
                        <div className="space-y-2">
                            <div className="mx-auto bg-primary/10 w-12 h-12 rounded-full flex items-center justify-center">
                                <Upload className="h-6 w-6 text-primary" />
                            </div>
                            <p className="text-sm font-medium">Click to upload video</p>
                            <p className="text-xs text-muted-foreground">MP4, WebM up to 50MB</p>
                            {error && <p className="text-xs text-red-500 font-medium mt-2">{error}</p>}
                        </div>
                    )}
                </div>
            ) : (
                <div className="relative border rounded-lg p-2 bg-muted/20">
                    <video
                        src={previewUrl}
                        className="w-full max-h-[200px] rounded object-cover bg-black"
                        controls
                    />
                    <Button
                        size="icon"
                        variant="destructive"
                        className="absolute -top-2 -right-2 h-6 w-6 rounded-full"
                        onClick={handleRemove}
                    >
                        <X className="h-3 w-3" />
                    </Button>
                    <div className="flex items-center gap-2 mt-2 text-xs text-green-600 font-medium px-1">
                        <CheckCircle className="h-3 w-3" />
                        Upload Complete
                    </div>
                </div>
            )}
        </div>
    )
}

"use client"

import * as React from 'react'
import { Button } from '@/components/ui/button'
import { Card } from '@/components/ui/card'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Bot, X, Send, Minimize2, User, Phone, Mail } from 'lucide-react'
import api from '@/lib/api' // Ensure this axios instance handles auth headers automatically if present

// Basic type for chat messages
interface Message {
    role: 'user' | 'model'
    text: string
}

export default function ChatWidget() {
    const [isOpen, setIsOpen] = React.useState(false)
    const [messages, setMessages] = React.useState<Message[]>([
        { role: 'model', text: 'Hi! I am TechWell Bot. How can I help you today?' }
    ])
    const [inputText, setInputText] = React.useState('')
    const [isThinking, setIsThinking] = React.useState(false)

    // Guest / Lead State
    const [isGuest, setIsGuest] = React.useState(true) // Default to guest, verified by checking token/localstorage usually
    const [hasProvidedDetails, setHasProvidedDetails] = React.useState(false)
    const [leadForm, setLeadForm] = React.useState({
        name: '',
        email: '',
        phone: ''
    })

    const scrollRef = React.useRef<HTMLDivElement>(null)

    // Check if user is logged in
    React.useEffect(() => {
        // Simple check: if token exists in localStorage, assume user
        const token = localStorage.getItem('token')
        if (token) {
            setIsGuest(false)
            setHasProvidedDetails(true) // Users don't need to fill form
        }
    }, [])

    // Auto-scroll
    React.useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollTop = scrollRef.current.scrollHeight
        }
    }, [messages, isOpen, hasProvidedDetails])

    const handleLeadSubmit = (e: React.FormEvent) => {
        e.preventDefault()
        if (leadForm.name && (leadForm.email || leadForm.phone)) {
            setHasProvidedDetails(true)
            // Optional: Send initial greeting to API to create lead immediately? 
            // Or just wait for first message. We wait for first message to keep it simple.
        }
    }

    const handleSendMessage = async () => {
        if (!inputText.trim()) return

        const userMsg = inputText.trim()
        setInputText('')
        setMessages(prev => [...prev, { role: 'user', text: userMsg }])
        setIsThinking(true)

        try {
            // Include leadDetails only if it's a guest session context
            const payload: any = {
                message: userMsg,
                history: messages.map(m => ({
                    role: m.role,
                    parts: [{ text: m.text }]
                }))
            }

            if (isGuest) {
                payload.leadDetails = leadForm
            }

            const res = await api.post('/ai/chat', payload)

            setMessages(prev => [...prev, { role: 'model', text: res.data.message }])
        } catch (error) {
            console.error(error)
            setMessages(prev => [...prev, { role: 'model', text: "I'm having trouble connecting. Try again?" }])
        } finally {
            setIsThinking(false)
        }
    }

    const handleKeyPress = (e: React.KeyboardEvent) => {
        if (e.key === 'Enter') handleSendMessage()
    }

    if (!isOpen) {
        return (
            <Button
                onClick={() => setIsOpen(true)}
                className="fixed bottom-6 right-6 h-14 w-14 rounded-full shadow-xl bg-purple-600 hover:bg-purple-700 text-white z-[9999] transition-all duration-300 hover:scale-110"
            >
                <Bot className="h-8 w-8" />
            </Button>
        )
    }

    return (
        <Card className="fixed bottom-6 right-6 w-[350px] h-[550px] shadow-2xl z-[9999] flex flex-col overflow-hidden border-purple-200 animate-in slide-in-from-bottom-10 fade-in duration-300">
            {/* Header */}
            <div className="bg-purple-600 p-4 flex justify-between items-center text-white shrink-0">
                <div className="flex items-center gap-2">
                    <div className="bg-white/20 p-1 rounded-full">
                        <Bot className="h-5 w-5" />
                    </div>
                    <div>
                        <h3 className="font-bold text-sm">TechWell Assistant</h3>
                        <p className="text-[10px] text-purple-100 flex items-center gap-1">
                            <span className="w-1.5 h-1.5 bg-green-400 rounded-full animate-pulse"></span>
                            Online  {isGuest ? 'Guest Support' : 'Student Support'}
                        </p>
                    </div>
                </div>
                <Button variant="ghost" size="icon" className="h-6 w-6 text-purple-100 hover:text-white hover:bg-purple-500" onClick={() => setIsOpen(false)}>
                    <Minimize2 className="h-4 w-4" />
                </Button>
            </div>

            {/* Content Area */}
            {hasProvidedDetails ? (
                <>
                    {/* Chat Area */}
                    <div className="flex-1 overflow-y-auto bg-slate-50 p-4 space-y-4" ref={scrollRef}>
                        {messages.map((msg, idx) => (
                            <div
                                key={idx}
                                className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}
                            >
                                <div
                                    className={`max-w-[85%] rounded-2xl px-4 py-2 text-sm shadow-sm whitespace-pre-wrap ${msg.role === 'user'
                                        ? 'bg-purple-600 text-white rounded-br-none'
                                        : 'bg-white text-slate-800 border rounded-bl-none'
                                        }`}
                                >
                                    {msg.text}
                                </div>
                            </div>
                        ))}
                        {isThinking && (
                            <div className="flex justify-start">
                                <div className="bg-white border rounded-2xl rounded-bl-none px-4 py-3 shadow-sm flex items-center gap-1">
                                    <span className="w-1.5 h-1.5 bg-slate-400 rounded-full animate-bounce [animation-delay:-0.3s]"></span>
                                    <span className="w-1.5 h-1.5 bg-slate-400 rounded-full animate-bounce [animation-delay:-0.15s]"></span>
                                    <span className="w-1.5 h-1.5 bg-slate-400 rounded-full animate-bounce"></span>
                                </div>
                            </div>
                        )}
                    </div>

                    {/* Input Area */}
                    <div className="p-3 bg-white border-t flex gap-2 shrink-0">
                        <Input
                            placeholder="Ask me anything..."
                            className="focus-visible:ring-purple-500"
                            value={inputText}
                            onChange={(e) => setInputText(e.target.value)}
                            onKeyDown={handleKeyPress}
                        />
                        <Button
                            size="icon"
                            className="bg-purple-600 hover:bg-purple-700"
                            onClick={handleSendMessage}
                            disabled={isThinking || !inputText.trim()}
                        >
                            <Send className="h-4 w-4" />
                        </Button>
                    </div>
                </>
            ) : (
                /* Guest Lead Form */
                <div className="flex-1 p-6 bg-slate-50 flex flex-col justify-center gap-3">
                    <div className="text-center mb-2">
                        <div className="bg-purple-100 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-3 text-purple-600">
                            <User className="h-8 w-8" />
                        </div>
                        <h3 className="font-bold text-gray-800">Welcome, Guest!</h3>
                        <p className="text-sm text-gray-500">Please introduce yourself to start chatting.</p>
                    </div>

                    <form onSubmit={handleLeadSubmit} className="space-y-4">
                        <div className="space-y-1">
                            <Label>Full Name <span className="text-red-500">*</span></Label>
                            <Input
                                required
                                placeholder="John Doe"
                                value={leadForm.name}
                                onChange={e => setLeadForm({ ...leadForm, name: e.target.value })}
                            />
                        </div>
                        <div className="space-y-1">
                            <Label>Mobile Number</Label>
                            <div className="relative">
                                <Phone className="absolute left-2.5 top-2.5 h-4 w-4 text-gray-400" />
                                <Input
                                    className="pl-9"
                                    placeholder="+91..."
                                    value={leadForm.phone}
                                    onChange={e => setLeadForm({ ...leadForm, phone: e.target.value })}
                                />
                            </div>
                        </div>
                        <div className="space-y-1">
                            <Label>Email</Label>
                            <div className="relative">
                                <Mail className="absolute left-2.5 top-2.5 h-4 w-4 text-gray-400" />
                                <Input
                                    className="pl-9"
                                    type="email"
                                    placeholder="john@example.com"
                                    value={leadForm.email}
                                    onChange={e => setLeadForm({ ...leadForm, email: e.target.value })}
                                />
                            </div>
                        </div>
                        <p className="text-xs text-gray-400 text-center">
                            We&apos;ll use this to contact you about your query.
                        </p>
                        <Button type="submit" className="w-full bg-purple-600 hover:bg-purple-700 mt-2">
                            Start Chatting
                        </Button>
                    </form>
                </div>
            )}
        </Card>
    )
}

