"use client"

import * as React from "react"

import type { ToastActionElement, ToastProps } from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
    id: string
    title?: React.ReactNode
    description?: React.ReactNode
    action?: ToastActionElement
}

const actionTypes = {
    ADD_TOAST: "ADD_TOAST",
    UPDATE_TOAST: "UPDATE_TOAST",
    DISMISS_TOAST: "DISMISS_TOAST",
    REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
    count = (count + 1) % Number.MAX_SAFE_INTEGER
    return count.toString()
}

type ActionType = typeof actionTypes

type Action =
    | {
        type: ActionType["ADD_TOAST"]
        toast: ToasterToast
    }
    | {
        type: ActionType["UPDATE_TOAST"]
        toast: Partial<ToasterToast>
    }
    | {
        type: ActionType["DISMISS_TOAST"]
        toastId?: ToasterToast["id"]
    }
    | {
        type: ActionType["REMOVE_TOAST"]
        toastId?: ToasterToast["id"]
    }

interface State {
    toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
    if (toastTimeouts.has(toastId)) {
        return
    }

    const timeout = setTimeout(() => {
        toastTimeouts.delete(toastId)
        dispatch({
            type: "REMOVE_TOAST",
            toastId: toastId,
        })
    }, TOAST_REMOVE_DELAY)

    toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
    switch (action.type) {
        case "ADD_TOAST":
            return {
                ...state,
                toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
            }

        case "UPDATE_TOAST":
            return {
                ...state,
                toasts: state.toasts.map((t) =>
                    t.id === action.toast.id ? { ...t, ...action.toast } : t
                ),
            }

        case "DISMISS_TOAST": {
            const { toastId } = action

            if (toastId) {
                addToRemoveQueue(toastId)
            } else {
                state.toasts.forEach((toast) => {
                    addToRemoveQueue(toast.id)
                })
            }

            return {
                ...state,
                toasts: state.toasts.map((t) =>
                    t.id === toastId || toastId === undefined
                        ? {
                            ...t,
                            open: false,
                        }
                        : t
                ),
            }
        }
        case "REMOVE_TOAST":
            if (action.toastId === undefined) {
                return {
                    ...state,
                    toasts: [],
                }
            }
            return {
                ...state,
                toasts: state.toasts.filter((t) => t.id !== action.toastId),
            }
    }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
    memoryState = reducer(memoryState, action)
    listeners.forEach((listener) => {
        listener(memoryState)
    })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
    const id = genId()

    const update = (props: ToasterToast) =>
        dispatch({
            type: "UPDATE_TOAST",
            toast: { ...props, id },
        })
    const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

    dispatch({
        type: "ADD_TOAST",
        toast: {
            ...props,
            id,
            open: true,
            onOpenChange: (open) => {
                if (!open) dismiss()
            },
        },
    })

    return {
        id: id,
        dismiss,
        update,
    }
}

function useToast() {
    const [state, setState] = React.useState<State>(memoryState)

    React.useEffect(() => {
        listeners.push(setState)
        return () => {
            const index = listeners.indexOf(setState)
            if (index > -1) {
                listeners.splice(index, 1)
            }
        }
    }, [state])

    return {
        ...state,
        toast,
        dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
    }
}

export { useToast, toast }

const fs = require('fs');
try {
    const report = JSON.parse(fs.readFileSync('lint_report_v2.json', 'utf8'));
    const ruleCounts = {};
    const filesWithIssues = [];

    let totalErrors = 0;
    let totalWarnings = 0;

    report.forEach(file => {
        if (file.messages.length > 0) {
            let errors = 0;
            let warnings = 0;
            file.messages.forEach(msg => {
                const ruleId = msg.ruleId || 'unknown';
                if (!ruleCounts[ruleId]) ruleCounts[ruleId] = { error: 0, warning: 0 };
                if (msg.severity === 2) {
                    ruleCounts[ruleId].error++;
                    errors++;
                    totalErrors++;
                } else {
                    ruleCounts[ruleId].warning++;
                    warnings++;
                    totalWarnings++;
                }
            });
            filesWithIssues.push({ path: file.filePath, errors, warnings, total: errors + warnings });
        }
    });

    let output = `Total Errors: ${totalErrors}\nTotal Warnings: ${totalWarnings}\n\n`;
    output += '--- Top Rules ---\n';

    // Sort by total occurrences
    Object.entries(ruleCounts)
        .sort((a, b) => (b[1].error + b[1].warning) - (a[1].error + a[1].warning))
        .forEach(([rule, counts]) => {
            output += `${rule}: ${counts.error} errors, ${counts.warning} warnings\n`;
        });

    output += '\n--- Top Files (by issue count) ---\n';
    filesWithIssues
        .sort((a, b) => b.total - a.total)
        .slice(0, 20)
        .forEach(f => {
            output += `${f.path} (E:${f.errors}, W:${f.warnings})\n`;
        });

    fs.writeFileSync('lint_summary.txt', output);
    console.log('Summary written to lint_summary.txt');

} catch (e) {
    console.error('Error processing report:', e);
}

